import React, { Component } from 'react';
import axios from "axios";
import "./Recipe.css";

class Recipe extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedItems: [],
      items: [
        "10 Bread loaves",
        "1L Milk Carton",
        "50g Mushrooms",
        "3 Eggs",
        "300g Rice",
        "5 Onions"
      ]
    };
  }

  toggleItem = (item) => {
    this.setState((prevState) => {
      const alreadySelected = prevState.selectedItems.includes(item);
      return {
        selectedItems: alreadySelected
          ? prevState.selectedItems.filter((i) => i !== item)
          : [...prevState.selectedItems, item]
      };
    });
  };

  handleGenerate = () => {
    alert("Generating recipe with: " + this.state.selectedItems.join(", "));
  };

  render() {
    return (
      <div className="recipe-container">
        <div className="recipe-card">
          <h2 className="recipe-title">AI Recipe Generator</h2>
          <p className="recipe-subtitle">Select items to incorporate:</p>

          <div className="recipe-list">
            {this.state.items.map((item, index) => (
              <label key={index}>
                <input
                  type="checkbox"
                  checked={this.state.selectedItems.includes(item)}
                  onChange={() => this.toggleItem(item)}
                />
                {item}
              </label>
            ))}
          </div>

          <button className="generate-btn" onClick={this.handleGenerate}>
            Generate
          </button>
        </div>
      </div>
    );
  }
}

export default Recipe;
