import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import supabase from "../supabaseClient";
import "./charity.css";

const GroceryList = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const foodbank = location.state?.foodbank || { name: "[UserName]" };
  const userId = location.state?.userId || null;

  const [selectedItems, setSelectedItems] = useState([]);
  const [foodItems, setFoodItems] = useState([]);
  const [fetchError, setFetchError] = useState(null);
  const [notification, setNotification] = useState(""); // inline notification
  const [notificationType, setNotificationType] = useState("success"); // success or warning

  // Fetch food items
  useEffect(() => {
    if (!userId) {
      setFetchError("No stakeholder ID provided.");
      return;
    }

    const fetchItems = async () => {
      const { data, error } = await supabase
        .from("fooditemdb")
        .select("fooditemid, name, Measure_per_Unit, Unit, quantity, expirydate")
        .eq("stakeholderid", userId);

      if (error) {
        console.error(error);
        setFetchError("Could not fetch food items.");
        setFoodItems([]);
        return;
      }

      const now = new Date();
      const cutoff = new Date(now.getTime() + 48 * 60 * 60 * 1000);

      const filteredItems = (data || []).filter((item) => {
        if (!item.expirydate) return true;
        const expiry = new Date(item.expirydate);
        return expiry > cutoff;
      });

      setFoodItems(filteredItems);
      setFetchError(null);
    };

    fetchItems();
  }, [userId]);

  const handleSelect = (item) => {
    setSelectedItems((prev) =>
      prev.includes(item) ? prev.filter((i) => i !== item) : [...prev, item]
    );
  };

  // Generate donation ID
  const generateDonationId = async () => {
    try {
      const { data, error } = await supabase
        .from("donationdb")
        .select("donationid")
        .order("donationid", { ascending: false })
        .limit(1);

      if (error) throw error;

      if (!data || data.length === 0) return "d001";

      const lastId = data[0].donationid;
      const lastNum = parseInt(lastId.replace("d", ""), 10);
      return `d${String(lastNum + 1).padStart(3, "0")}`;
    } catch (err) {
      console.error("Error generating donation ID:", err);
      return `d${String(Math.floor(1 + Math.random() * 999)).padStart(3, "0")}`;
    }
  };

  // Send donation
  const handleSendDonation = async () => {
    if (selectedItems.length === 0) {
      setNotificationType("warning");
      setNotification("Please select at least one item to donate.");
      return;
    }

    try {
      const itemsToSave = selectedItems.map((label) => {
        const [name] = label.split(" (");
        return { name: name.trim() };
      });

      const newDonationId = await generateDonationId();

      const { data, error } = await supabase
        .from("donationdb")
        .insert([
          {
            donationid: newDonationId,
            donationstatus: "pending",
            charityid: foodbank.stakeholderid,
            stakeholderid: userId,
            donation_items: itemsToSave,
          },
        ])
        .select();

      if (error) {
        console.error("Error inserting donation:", error);
        setNotificationType("warning");
        setNotification("Could not send donation. Please try again.");
        return;
      }

      console.log("Donation saved:", data[0]);
      setNotificationType("success");
      setNotification(`Donation sent to ${foodbank.name}`);
      setSelectedItems([]); // clear selections
    } catch (err) {
      console.error("Unexpected error:", err);
      setNotificationType("warning");
      setNotification("Something went wrong.");
    }
  };

  // Subscribe to donation status updates
  useEffect(() => {
    if (!userId) return;

    const channel = supabase
      .channel(`donation-updates-${userId}`)
      .on(
        "postgres_changes",
        {
          event: "UPDATE",
          schema: "public",
          table: "donationdb",
          filter: `stakeholderid=eq.${userId}`,
        },
        (payload) => {
          const newStatus = payload.new.donationstatus;
          const donationId = payload.new.donationid;
          console.log("Realtime payload:", payload);

          if (newStatus === "approved") {
            setNotificationType("success");
            setNotification(`Donation ${donationId} has been approved!`);
          } else if (newStatus === "unapproved") {
            setNotificationType("warning");
            setNotification(`Donation ${donationId} has been declined.`);
          }
        }
      )
      .subscribe();

    return () => {
      supabase.removeChannel(channel);
    };
  }, [userId]);

  return (
    <div className="grocery-container">
      <button className="back-btn" onClick={() => navigate("/donor")}>
        ← Back
      </button>

      <h3>Donate to {foodbank.name}</h3>
      <h3> <em> Select items to donate </em></h3>

      {fetchError && <p>{fetchError}</p>}

      <div className="food-list green-bg">
        {foodItems.map((item) => {
          const itemLabel = `${item.name} (${item.Measure_per_Unit} ${item.Unit}, quantity: ${item.quantity})`;
          return (
            <label key={item.fooditemid} className="food-item">
              <input
                type="checkbox"
                checked={selectedItems.includes(itemLabel)}
                onChange={() => handleSelect(itemLabel)}
              />
              {itemLabel}{" "}
              <span style={{ fontSize: "0.8em", color: "#666" }}>
                (expires {item.expirydate ? new Date(item.expirydate).toLocaleDateString() : "N/A"})
              </span>
            </label>
          );
        })}
      </div>

      {/* Inline notification above Send Donation button */}
      {notification && (
        <div className={`inline-notification ${notificationType}`}>
          {notification}
        </div>
      )}

      <button className="btn-send" onClick={handleSendDonation}>
        Send Donation
      </button>
    </div>
  );
};

export default GroceryList;
