/* B Shimelis _ 23554691_ 16 September: Auth Login (updated) */

import { useState } from "react";
import axios from "axios";

export default function LoginForm({ goToRegister, onLogin }) {
  const [form, setForm] = useState({ email: "", password: "" });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(
        "https://foodsave-backend.onrender.com/api/auth/login",
        form
      );

      // optional: if you add JWT later
      // localStorage.setItem("token", res.data.token);

      setSuccess(res.data.message || "Login successful!");
      setError("");

      if (onLogin) onLogin(res.data.user); // pass user up to App
    } catch (err) {
      console.error("Login error:", err);
      setError(err.response?.data?.error || "Invalid email or password");
      setSuccess("");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="form-card">
      <h1>FOODSAVE HUB</h1>

      <input
        type="email"
        name="email"
        placeholder="Email"
        value={form.email}
        onChange={handleChange}
        required
      />
      <input
        type="password"
        name="password"
        placeholder="Password"
        value={form.password}
        onChange={handleChange}
        required
      />

      {error && <p className="error-msg">{error}</p>}
      {success && <p className="success-msg">{success}</p>}

      <button type="submit">Login</button>
      <button
        type="button"
        className="toggle-link"
        onClick={goToRegister}
      >
        Don't have an account? Sign Up
      </button>
    </form>
  );
}
