import React, { Component } from 'react';

class Notification extends Component {
  constructor(props) {
    super(props);
    this.state = {
      notifications: [], // start with an empty array
      loading: true,
      error: null
    };
  }

  componentDidMount() {
    // Fetch notifications from your backend
    fetch('http://localhost:5000/notifications')
      .then((res) => res.json())
      .then((data) => {
        // Ensure data is an array
        if (Array.isArray(data)) {
          this.setState({ notifications: data, loading: false });
        } else {
          console.error('Data is not an array', data);
          this.setState({ notifications: [], loading: false });
        }
      })
      .catch((err) => {
        console.error(err);
        this.setState({ error: 'Failed to fetch notifications', loading: false });
      });
  }

  render() {
    const { notifications, loading, error } = this.state;

    if (loading) return <p style={{ textAlign: 'center', marginTop: '50px' }}>Loading...</p>;
    if (error) return <p style={{ textAlign: 'center', marginTop: '50px', color: 'red' }}>{error}</p>;

    return (
      <div style={{ textAlign: 'center', marginTop: '50px' }}>
        <h2>Notifications Page</h2>
        <ul>
          {notifications.map((note) => (
            <li key={note.id}>{note.message}</li>
          ))}
        </ul>
      </div>
    );
  }
}

export default Notification;
