import { useState, useEffect, useMemo } from "react";
import axios from "axios";
import '../auth.css';

const API_BACKEND = process.env.REACT_APP_API_BACKEND;

const foodEmojis = [
  '🍎','🍌','🍊','🍐','🍉','🍇','🍓',
  '🥕','🌽','🥔','🥦','🥬','🧄','🧅',
  '🍞','🥖','🥯','🥐',
  '🥚','🧀','🥛',
  '🥫','🫙','🍲','🥣',
  '🍚','🍝','🌾',
  '🥜','🌰','🍯','🍼',
  '🧺','🍪','🍫'
];

export default function LoginForm({ goToRegister, onLogin }) {
  const [form, setForm] = useState({ email: "", password: "" });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [emojiIndex, setEmojiIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => setEmojiIndex(prev => (prev + 1) % foodEmojis.length), 1000);
    return () => clearInterval(interval);
  }, []);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(`${API_BACKEND}/api/auth/login`, form);
      setSuccess(res.data.message || "Login successful!");
      setError("");
      if (onLogin) onLogin(res.data.user);
      localStorage.setItem("loggedInUser", JSON.stringify(res.data.user));
    } catch (err) {
      setError(err.response?.data?.error || "Invalid email or password");
      setSuccess("");
    }
  };

  const emojiSpans = useMemo(() => {
    return Array.from({ length: 100 }, (_, i) => {
      const emoji = foodEmojis[i % foodEmojis.length];
      const left = Math.random() * 100;
      const duration = 10 + Math.random() * 6;
      const delay = Math.random() * 10;
      return (
        <span
          key={i}
          className="food-item"
          style={{ left: `${left}%`, fontSize: '60px', animationDuration: `${duration}s`, animationDelay: `${delay}s`, opacity: 0, animationFillMode: 'forwards' }}
        >
          {emoji}
        </span>
      );
    });
  }, []);

  return (
    <div className="auth-page">
      <div className="food-background">{emojiSpans}</div>

      <form onSubmit={handleSubmit} className="auth-card vibrant-card">
        <div className="form-header">
          <div className="logo-icon" style={{ fontSize: '3rem' }}>{foodEmojis[emojiIndex]}</div>
          <h1 className="app-title">FoodSave Hub</h1>
          <p className="subtitle"><em>Recreate, Donate, and Reduce Food Waste</em></p>
        </div>

        <input type="email" name="email" placeholder="Enter your email" value={form.email} onChange={handleChange} required className="input-field" />
        <input type="password" name="password" placeholder="Enter your password" value={form.password} onChange={handleChange} required className="input-field" />

        {error && <p className="error-msg">{error}</p>}
        {success && <p className="success-msg">{success}</p>}

        <button type="submit" className="primary-btn">Login</button>
        <button type="button" className="toggle-link" onClick={goToRegister}>
          Don't have an account? <span>Sign Up</span>
        </button>
      </form>
    </div>
  );
}
