import React, { useState, useEffect, useMemo } from "react";
import axios from "axios";
import "./Profile.css"; // localized CSS

const API_BACKEND = process.env.REACT_APP_API_BACKEND;

const regions = [ 
  "Durban, KwaZulu-Natal","Cape Town, Western Cape","Pretoria, Gauteng",
  "Johannesburg, Gauteng","Port Elizabeth (Gqeberha), Eastern Cape",
  "Bloemfontein, Free State","Nelspruit (Mbombela), Mpumalanga",
  "Kimberley, Northern Cape","Potchefstroom, North West","Polokwane, Limpopo",
  "George, Western Cape","East London, Eastern Cape","Welkom, Free State",
  "Mthatha, Eastern Cape","Stellenbosch, Western Cape","Pietermaritzburg, KwaZulu-Natal"
];

export default function Profile({ currentUser, onLogout }) {
  const [userData, setUserData] = useState(null);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({});
  const [message, setMessage] = useState("");
  const [regionSearch, setRegionSearch] = useState("");
  const [showDropdown, setShowDropdown] = useState(false);
  const [changingPassword, setChangingPassword] = useState(false);
  const [otpStep, setOtpStep] = useState(false);
  const [otp, setOtp] = useState("");
  const [resendCooldown, setResendCooldown] = useState(0);

  useEffect(() => {
    if (currentUser) {
      setUserData(currentUser);
      setFormData({
        name: currentUser.name,
        region: currentUser.region,
        capacity: currentUser.capacity || "",
        newPassword: "",
        confirmPassword: "",
        push_notifications: currentUser.push_notifications === 1
      });
      setRegionSearch(currentUser.region);
    }
  }, [currentUser]);

  useEffect(() => {
    if (resendCooldown <= 0) return;
    const timer = setInterval(() => setResendCooldown(prev => prev - 1), 1000);
    return () => clearInterval(timer);
  }, [resendCooldown]);

  const filteredRegions = useMemo(() => {
    return regions.filter(r => r.toLowerCase().includes(regionSearch.toLowerCase()));
  }, [regionSearch]);

  const handleChange = (e) => setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  const handleRegionSelect = (region) => {
    setFormData(prev => ({ ...prev, region }));
    setRegionSearch(region);
    setShowDropdown(false);
  };

  const handleEditToggle = () => {
    setEditing(!editing);
    setMessage("");
  };

  const passwordValid = (pw) => {
    return /[A-Z]/.test(pw) && /[a-z]/.test(pw) && /\d/.test(pw) && /[!@#$%^&*]/.test(pw) && pw.length >= 8;
  };

  const requestOtp = async () => {
    try {
      await axios.post(`${API_BACKEND}/api/auth/request-otp`, { email: userData.email });
      setMessage("OTP sent to your email!");
      setOtpStep(true);
      setResendCooldown(30);
    } catch (err) {
      setMessage(err.response?.data?.error || "Failed to request OTP");
    }
  };

  const verifyOtp = async () => {
    try {
      await axios.post(`${API_BACKEND}/api/auth/verify-otp`, { email: userData.email, otp });
      setMessage("OTP verified! Enter new password below.");
      setOtpStep(false);
    } catch (err) {
      setMessage(err.response?.data?.error || "OTP verification failed");
    }
  };

 const handleSave = async () => {
  if (!formData.currentPassword) {
    setMessage("Please enter your current password to update profile.");
    return;
  }
  if (formData.newPassword && formData.newPassword !== formData.confirmPassword) {
    setMessage("Passwords do not match!");
    return;
  }

  try {
    const payload = {
      email: userData.email,
      currentPassword: formData.currentPassword,
      name: formData.name,
      region: formData.region,
      capacity: userData.stakeholderID?.startsWith("c") ? Number(formData.capacity) : undefined,
      newPassword: formData.newPassword,
      push_notifications: formData.push_notifications ? 1 : 0
    };

    const res = await axios.put(`${API_BACKEND}/profile/update`, payload);
    setUserData(res.data.user);
    setEditing(false);
    setMessage("Profile updated successfully!");
  } catch (err) {
    console.error(err);
    setMessage(err.response?.data?.error || "Failed to update profile.");
  }
};

  if (!userData) return <p>Loading...</p>;

  return (
    <div className="profile-page-local">
      <div className="profile-card-local">
        <h2>User Profile</h2>

        <div className="profile-row-local">
          <label>Email:</label>
          <span>{userData.email}</span>
        </div>

        <div className="profile-row-local">
          <label>Name:</label>
          {editing ? (
            <input type="text" name="name" value={formData.name} onChange={handleChange} className="input-field-local"/>
          ) : <span>{userData.name}</span>}
        </div>

        <div className="profile-row-local">
          <label>Region:</label>
          {editing ? (
            <div className="region-search-container-local">
              <input type="text" value={regionSearch} onChange={(e)=>{setRegionSearch(e.target.value); setFormData(prev=>({...prev, region:e.target.value})); setShowDropdown(true)}} 
                onFocus={()=>setShowDropdown(true)} onBlur={()=>setTimeout(()=>setShowDropdown(false),150)} className="input-field-local"/>
              {showDropdown && filteredRegions.length>0 && (
                <div className="region-dropdown-local">
                  {filteredRegions.map((r,i)=>(
                    <div key={i} className="region-option-local" onClick={()=>handleRegionSelect(r)}>{r}</div>
                  ))}
                </div>
              )}
            </div>
          ) : <span>{userData.region}</span>}
        </div>

        {userData.stakeholderID?.startsWith("c") && (
          <div className="profile-row-local">
            <label>Capacity:</label>
            {editing ? (
              <input type="number" name="capacity" value={formData.capacity} onChange={handleChange} className="input-field-local"/>
            ) : <span>{userData.capacity}</span>}
          </div>
        )}

        {editing && (
          <>
            <div className="profile-row-local">
              <label>Push Notifications:</label>
              <input type="checkbox" checked={formData.push_notifications} onChange={()=>setFormData(prev=>({...prev, push_notifications: !prev.push_notifications}))}/>
            </div>

            <div className="profile-row-local">
              <label>Change Password?</label>
              <input type="checkbox" checked={changingPassword} onChange={()=>{setChangingPassword(!changingPassword); setOtpStep(!changingPassword)}}/>
            </div>

            {changingPassword && otpStep && (
              <div className="profile-row-local">
                <label>Verify OTP:</label>
                <input type="text" value={otp} onChange={(e)=>setOtp(e.target.value)} className="input-field-local"/>
                <button type="button" onClick={verifyOtp} disabled={resendCooldown>0}>
                  {resendCooldown>0 ? `Resend OTP in ${resendCooldown}s` : "Verify OTP / Resend"}
                </button>
                <button type="button" onClick={requestOtp}>Request OTP</button>
              </div>
            )}

            {changingPassword && !otpStep && (
              <>
                <div className="profile-row-local">
                  <label>New Password:</label>
                  <input type="password" name="newPassword" value={formData.newPassword} onChange={handleChange} className="input-field-local"/>
                </div>
                <div className="profile-row-local">
                  <label>Confirm Password:</label>
                  <input type="password" name="confirmPassword" value={formData.confirmPassword} onChange={handleChange} className="input-field-local"/>
                </div>
              </>
            )}
          </>
        )}

        <div className="profile-actions-local">
          {editing ? (
            <>
              <button className="btn-save-local" onClick={handleSave}>Save</button>
              <button className="btn-cancel-local" onClick={handleEditToggle}>Cancel</button>
            </>
          ) : (
            <button className="btn-edit-local" onClick={handleEditToggle}>Edit Profile</button>
          )}
        </div>

        {message && <p className="profile-message-local">{message}</p>}
      </div>
    </div>
  );
}
