// src/bulkupload/BulkUpload.js
import React, { Component } from 'react';
import './BulkUpload.css';

class BulkUpload extends Component {
  constructor(props) {
    super(props);
    this.state = {
      file: null,
      error: '',
      success: '',
      preview: []
    };
  }

  handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    if (!file.name.endsWith('.xlsx')) {
      this.setState({ error: 'Invalid file type. Please upload an Excel (.xlsx) file.', success: '' });
    } else {
      this.setState({ file, error: '', success: '' });
    }
  };

  uploadFile = async () => {
    if (!this.state.file) return this.setState({ error: 'Please choose an Excel (.xlsx) file.' });

    const formData = new FormData();
    formData.append('file', this.state.file);

    try {
      const API_URL = process.env.REACT_APP_API_URL;
      const res = await fetch(`${API_URL}/api/bulkupload`, {
        method: 'POST',
        body: formData,
      });
      const data = await res.json();
      if (res.ok) {
        this.setState({ success: `${data.count} items uploaded successfully.`, error: '', preview: [] });
      } else {
        this.setState({ error: data.error || 'Validation failed', success: '' });
        if (data.details) console.warn(data.details);
      }
    } catch (err) {
      this.setState({ error: `Server error: ${err.message}`, success: '' });
    }
  };

  render() {
    return (
      <div className="bulk-container">
        <h2 className="bulk-title">Bulk Upload Excel (.xlsx)</h2>

        <input type="file" accept=".xlsx" onChange={this.handleFileChange} className="bulk-input" />
        <button onClick={this.uploadFile} className="bulk-btn">Upload</button>

        {this.state.error && <div className="bulk-error">{this.state.error}</div>}
        {this.state.success && <div className="bulk-success">{this.state.success}</div>}
      </div>
    );
  }
}

export default BulkUpload;
