import React, { Component } from "react";
import axios from "axios";
import "./Recipe.css";

class Recipe extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedItems: [],
      items: [
        "10 Bread loaves",
        "1L Milk Carton",
        "50g Mushrooms",
        "3 Eggs",
        "300g Rice",
        "5 Onions"
      ],
      recipes: [],
      loading: false,
      error: "",
      activeFilters: [], // e.g., ["Vegan", "Gluten-Free"]
      selectedRecipe: null
    };
  }

  toggleItem = (item) => {
    this.setState((prevState) => {
      const alreadySelected = prevState.selectedItems.includes(item);
      return {
        selectedItems: alreadySelected
          ? prevState.selectedItems.filter((i) => i !== item)
          : [...prevState.selectedItems, item]
      };
    });
  };

  handleFilter = (filter) => {
    this.setState((prevState) => {
      const activeFilters = prevState.activeFilters.includes(filter)
        ? prevState.activeFilters.filter(f => f !== filter)
        : [...prevState.activeFilters, filter];
      return { activeFilters };
    });
  };

  handleGenerate = async () => {
    const { selectedItems } = this.state;
    if (selectedItems.length === 0) {
      this.setState({ error: "Please select at least one ingredient" });
      return;
    }

    this.setState({ loading: true, error: "", recipes: [] });

    try {
      // Simulate API call
      const res = await axios.post("https://deepseek.api/recipes", {
        ingredients: selectedItems
      }, { timeout: 30000 }); // 30s timeout

      this.setState({ recipes: res.data, loading: false });
    } catch (err) {
      console.error(err);
      this.setState({ loading: false, error: "Recipe service unavailable" });
    }
  };

  toggleSaveRecipe = (recipeId) => {
    this.setState(prevState => ({
      recipes: prevState.recipes.map(r =>
        r.id === recipeId ? { ...r, saved: !r.saved } : r
      )
    }));
  };

  render() {
    const { selectedItems, items, recipes, loading, error, activeFilters, selectedRecipe } = this.state;

    // Apply filters
    const filteredRecipes = recipes.filter(r => 
      activeFilters.every(f => r.dietary?.includes(f))
    );

    return (
      <div className="recipe-container">
        <div className="recipe-card">
          <h2 className="recipe-title">AI Recipe Generator</h2>
          <p className="recipe-subtitle">Select items to incorporate:</p>

          <div className="recipe-list">
            {items.map((item, index) => (
              <label key={index}>
                <input
                  type="checkbox"
                  checked={selectedItems.includes(item)}
                  onChange={() => this.toggleItem(item)}
                />
                {item}
              </label>
            ))}
          </div>

          <button
            className="generate-btn"
            onClick={this.handleGenerate}
            disabled={loading}
          >
            {loading ? "Generating..." : "Generate"}
          </button>

          {error && <p className="error-msg">{error}</p>}

          <div className="filter-buttons">
            {["Vegan", "Vegetarian", "Gluten-Free"].map(f => (
              <button
                key={f}
                className={`filter-btn ${activeFilters.includes(f) ? "active" : ""}`}
                onClick={() => this.handleFilter(f)}
              >
                {f}
              </button>
            ))}
          </div>

          <div className="recipe-results">
            {loading && <p>Loading recipes...</p>}

            {filteredRecipes.map(recipe => (
              <div key={recipe.id} className="recipe-result-card" onClick={() => this.setState({ selectedRecipe: recipe })}>
                <img src={recipe.image} alt={recipe.title} />
                <h3>{recipe.title}</h3>
                <p>Prep Time: {recipe.prepTime} mins</p>
                {recipe.dietary?.map(d => (
                  <span key={d} className="badge">{d}</span>
                ))}
                <button
                  className="save-btn"
                  onClick={(e) => { e.stopPropagation(); this.toggleSaveRecipe(recipe.id); }}
                >
                  {recipe.saved ? "‚ù§Ô∏è" : "ü§ç"}
                </button>
              </div>
            ))}
          </div>

          {selectedRecipe && (
            <div className="recipe-detail">
              <h3>{selectedRecipe.title}</h3>
              <p>Ingredients: {selectedRecipe.ingredients.join(", ")}</p>
              <p>Instructions: {selectedRecipe.instructions}</p>
              <p>Nutrition: {selectedRecipe.nutrition}</p>
              <p>Serving Size: {selectedRecipe.servingSize}</p>
              <button onClick={() => this.setState({ selectedRecipe: null })}>Close</button>
            </div>
          )}
        </div>
      </div>
    );
  }
}

export default Recipe;
