import React, { useState } from "react";
import axios from "axios";

export default function HomeForm({ currentUser, onClose, onRefresh }) {
  const [form, setForm] = useState({
    name: "",
    expiryDate: "",
    quantity: 1,
    foodCategory: "",
    measurePerUnit: 1,
    unit: "g",
  });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const foodCategories = [
    "Produce",
    "Dairy & Eggs",
    "Meat",
    "Seafood",
    "Bakery",
    "Grains",
    "Frozen Foods",
    "Beverages",
    "Snacks",
    "Prepared Meals",
    "Other"
  ];

  const units = ["ml", "L", "g", "kg"];

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.name) return setError("Name is required");
    if (!form.expiryDate || new Date(form.expiryDate) <= new Date())
      return setError("Expiry date must be in the future");
    if (form.quantity <= 0) return setError("Quantity must be at least 1");
    if (form.measurePerUnit <= 0) return setError("Measure per unit must be at least 1");
    if (!form.foodCategory) return setError("Please select a food category");

    try {
      await axios.post("https://foodsave-backend.onrender.com/api/users/fooditems", {
        ...form,
        stakeholderID: currentUser.stakeholderID
      });
      setSuccess("Food item added!");
      setError("");
      setForm({
        name: "",
        expiryDate: "",
        quantity: 1,
        foodCategory: "",
        measurePerUnit: 1,
        unit: "g",
      });
      if (onRefresh) onRefresh();
    } catch (err) {
      setError(err.response?.data?.error || "Failed to add item");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="form-card">
      <h1>Add Food Item</h1>

      <input type="text" name="name" placeholder="Name" value={form.name} onChange={handleChange} required />
      <input type="date" name="expiryDate" value={form.expiryDate} onChange={handleChange} required />
      <input type="number" name="quantity" min="1" value={form.quantity} onChange={handleChange} required />

      <select name="foodCategory" value={form.foodCategory} onChange={handleChange} required>
        <option value="">Select Category</option>
        {foodCategories.map(cat => (
          <option key={cat} value={cat}>{cat}</option>
        ))}
      </select>

      <input type="number" name="measurePerUnit" min="1" value={form.measurePerUnit} onChange={handleChange} required />
      <select name="unit" value={form.unit} onChange={handleChange} required>
        {units.map(u => <option key={u} value={u}>{u}</option>)}
      </select>

      {error && <p className="error-msg">{error}</p>}
      {success && <p className="success-msg">{success}</p>}

      <button type="submit">Add Item</button>
      <button type="button" className="toggle-link" onClick={onClose}>Back to Inventory</button>
    </form>
  );
}
