import React from 'react';

const sampleItems = [
  {name:'Bread loaves', qty:10, expires:'2 days'},
  {name:'Mushrooms', qty:50, expires:'6 days'},
  {name:'Milk Cartons', qty:1, expires:'2 weeks'}
];

export default function Home(){
  return (
    <div style={{fontFamily:'Arial, sans-serif', padding:20}}>
      <h1 style={{color:'#2b7a78'}}>FOODSAVE HUB</h1>
      <p style={{color:'#555'}}>Your food, your good — track, donate, and reduce waste.</p>
      <section style={{display:'flex', gap:20}}>
        <div style={{flex:1, padding:10, border:'1px solid #eee', borderRadius:8}}>
          <h2>Current Foods</h2>
          <ul>
            {sampleItems.map((it,i)=>(
              <li key={i}>{it.qty} x {it.name} — {it.expires}</li>
            ))}
          </ul>
        </div>
        <div style={{width:320, padding:10, border:'1px solid #eee', borderRadius:8}}>
          <h3>Ongoing Chats</h3>
          <div style={{background:'#f7f7f7', padding:8, borderRadius:6}}>Nosh Food Rescue: "Let's discuss pickup"</div>
        </div>
      </section>
    </div>
  )
}
