/* Author: Bethlehem Shimelis
   Event: Sprint 1: Manually Input Food Items with Expiry dates
   LatestUpdate: Cartoon-style floating food background with dynamic donation logo
   Parameters: 
      goToRegister (function) - callback to switch to registration form
      onLogin (function) - callback after successful login
   Description: Handles user login with visually appealing floating food theme
*/

import { useState, useEffect, useMemo } from "react";
import axios from "axios";
import '../auth.css'; // Include the CSS we worked on

const API_BACKEND = process.env.REACT_APP_API_BACKEND;

const foodEmojis = [
 'üçé', 'üçå', 'üçä', 'üçê', 'üçâ', 'üçá', 'üçì', // fruits
  'ü•ï', 'üåΩ', 'ü•î', 'ü•¶', 'ü•¨', 'üßÑ', 'üßÖ', // vegetables
  'üçû', 'ü•ñ', 'ü•Ø', 'ü•ê',                   // breads
  'ü•ö', 'üßÄ', 'ü•õ',                         // dairy & eggs
  'ü•´', 'ü´ô', 'üç≤', 'ü•£',                   // canned / packaged foods
  'üçö', 'üçù', 'üåæ',                          // grains/pasta/rice
  'ü•ú', 'üå∞',                               // nuts
  'üçØ',                                      // honey
  'üçº',                                      // baby formula
  'üß∫',                                      // donation basket
  'üç™', 'üç´'                                // optional packaged snacks
];

export default function LoginForm({ goToRegister, onLogin }) {
  const [form, setForm] = useState({ email: "", password: "" });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [emojiIndex, setEmojiIndex] = useState(0);

  // Cycle the logo emoji
  useEffect(() => {
    const interval = setInterval(() => {
      setEmojiIndex(prev => (prev + 1) % foodEmojis.length);
    }, 1000); // Change emoji every second
    return () => clearInterval(interval);
  }, []);

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(`${API_BACKEND}/api/auth/login`, form);
      setSuccess(res.data.message || "Login successful!");
      setError("");

      if (onLogin) onLogin(res.data.user);
      localStorage.setItem("loggedInUser", JSON.stringify(res.data.user));
    } catch (err) {
      console.error("Login error:", err);
      setError(err.response?.data?.error || "Invalid email or password");
      setSuccess("");
    }
  };

  // Floating food emojis
  const emojiSpans = useMemo(() => {
    return Array.from({ length: 100 }, (_, i) => {
      const emoji = foodEmojis[i % foodEmojis.length];
      const left = Math.random() * 100;
      const duration = 10 + Math.random() * 6; // 10‚Äì16s
      const delay = Math.random() * 10;        // 0‚Äì10s random delay

      return (
        <span
          key={i}
          className="food-item"
          style={{
            left: `${left}%`,
            fontSize: '60px',
            animationDuration: `${duration}s`,
            animationDelay: `${delay}s`,
            opacity: 0,
            animationFillMode: 'forwards'
          }}
        >
          {emoji}
        </span>
      );
    });
  }, []);

  return (
    <div className="login-page">
      {/* Food background */}
      <div className="food-background">{emojiSpans}</div>

      {/* Login card */}
      <form onSubmit={handleSubmit} className="form-card">
        <div className="form-header">
          <div className="logo-icon" style={{ fontSize: '3rem' }}>
            {foodEmojis[emojiIndex]}
          </div>
          <h1 className="app-title">FoodSave Hub</h1>
          <p className="subtitle"> <em> Recreate, Donate, and Reduce Food Waste </em> </p>
        </div>

        <input
          type="email"
          name="email"
          placeholder="Enter your email"
          value={form.email}
          onChange={handleChange}
          required
          className="input-field"
        />
        <input
          type="password"
          name="password"
          placeholder="Enter your password"
          value={form.password}
          onChange={handleChange}
          required
          className="input-field"
        />

        {error && <p className="error-msg">{error}</p>}
        {success && <p className="success-msg">{success}</p>}

        <button type="submit" className="primary-btn">
          Login
        </button>
        <button type="button" className="toggle-link" onClick={goToRegister}>
          Don't have an account? <span>Sign Up</span>
        </button>
      </form>
    </div>
  );
}
