import React, { useEffect, useState } from "react";
import axios from "axios";

export default function HomeDash({ currentUser, onEditItem }) {
  const [foodItems, setFoodItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const fetchFoodItems = async () => {
    try {
      const res = await axios.get(
        `https://foodsave-backend.onrender.com/api/users/fooditems?email=${currentUser.email}`
      );
      setFoodItems(res.data.foodItems);
      setLoading(false);
    } catch (err) {
      console.error(err);
      setError("Failed to load items");
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this item?")) return;

    try {
      await axios.delete(
        `https://foodsave-backend.onrender.com/api/users/fooditems/${id}`,
        { data: { email: currentUser.email } }
      );
      setFoodItems(foodItems.filter((item) => item.fooditemid !== id));
    } catch (err) {
      console.error(err);
      setError("Failed to delete item");
    }
  };

  useEffect(() => {
    fetchFoodItems();
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p className="error-msg">{error}</p>;

  return (
    <div className="dashboard-container">
      <h2>My Food Items</h2>
      <table className="food-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Expiry</th>
            <th>Qty</th>
            <th>Category</th>
            <th>Measure</th>
            <th>Unit</th>
            <th>Donation ID</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {foodItems.map((item) => (
            <tr key={item.fooditemid}>
              <td>{item.name}</td>
              <td>{new Date(item.expirydate).toLocaleDateString()}</td>
              <td>{item.quantity}</td>
              <td>{item.foodcategory}</td>
              <td>{item.Measure_per_Unit}</td>
              <td>{item.Unit}</td>
              <td>{item.donationid || "-"}</td>
              <td>
                <button
                  className="delete-btn"
                  onClick={() => handleDelete(item.fooditemid)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
