import React, { Component } from 'react';
import axios from "axios";
import "./Recipe.css";

class Recipe extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedItems: [],
      items: [
        "10 Bread loaves",
        "1L Milk Carton",
        "50g Mushrooms",
        "3 Eggs",
        "300g Rice",
        "5 Onions"
      ],
      filter: "", // dropdown filter
      recipes: [], // store recipes from API
      loading: false,
      error: ""
    };
  }

  toggleItem = (item) => {
    this.setState((prevState) => {
      const alreadySelected = prevState.selectedItems.includes(item);
      return {
        selectedItems: alreadySelected
          ? prevState.selectedItems.filter((i) => i !== item)
          : [...prevState.selectedItems, item]
      };
    });
  };

  handleFilterChange = (e) => {
    this.setState({ filter: e.target.value });
  };

  handleGenerate = async () => {
    if (this.state.selectedItems.length === 0) {
      this.setState({ error: "Please select at least one ingredient" });
      return;
    }

    this.setState({ loading: true, error: "", recipes: [] });

    try {
      // Fake API call for demo purposes
      const response = await axios.post("/api/recipes", {
        ingredients: this.state.selectedItems,
        filter: this.state.filter
      });

      this.setState({ recipes: response.data });
    } catch (err) {
      this.setState({ error: "Recipe service unavailable" });
    } finally {
      this.setState({ loading: false });
    }
  };

  render() {
    const { selectedItems, items, filter, recipes, loading, error } = this.state;

    return (
      <div className="recipe-container">
        <div className="recipe-card">
          <h2 className="recipe-title">AI Recipe Generator</h2>
          <p className="recipe-subtitle">Select items to incorporate:</p>

          <div className="recipe-list">
            {items.map((item, index) => (
              <label key={index}>
                <input
                  type="checkbox"
                  checked={selectedItems.includes(item)}
                  onChange={() => this.toggleItem(item)}
                />
                {item}
              </label>
            ))}
          </div>

          {/* Filter Dropdown */}
          <div className="filter-dropdown">
            <label htmlFor="recipe-filter">Filter Recipes: </label>
            <select id="recipe-filter" value={filter} onChange={this.handleFilterChange}>
              <option value="">All</option>
              <option value="Vegan">Vegan</option>
              <option value="Vegetarian">Vegetarian</option>
              <option value="Gluten-Free">Gluten-Free</option>
            </select>
          </div>

          <button className="generate-btn" onClick={this.handleGenerate} disabled={loading}>
            {loading ? "Generating..." : "Generate"}
          </button>

          {error && <p className="error-msg">{error}</p>}

          <div className="recipe-results">
            {recipes.map((recipe, index) => (
              <div key={index} className="recipe-result-card">
                <img src={recipe.image} alt={recipe.title} />
                <h3>{recipe.title}</h3>
                <p>Prep: {recipe.prepTime}</p>
                {recipe.badges?.map((badge, i) => (
                  <span key={i} className="badge">{badge}</span>
                ))}
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }
}

export default Recipe;
