// src/users/HomeDash.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";

export default function HomeDash({ currentUser }) {
  const [foodItems, setFoodItems] = useState([]);

  const fetchFoodItems = async () => {
    if (!currentUser) return;
    try {
      const res = await axios.get(`https://foodsave-backend.onrender.com/api/users/fooditems?stakeholderID=${currentUser.stakeholderID}`);
      setFoodItems(res.data.foodItems);
    } catch (err) {
      console.error(err);
    }
  };

  const deleteItem = async (id) => {
    try {
      await axios.delete(`https://foodsave-backend.onrender.com/api/users/fooditems/${id}`);
      fetchFoodItems();
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchFoodItems();
  }, [currentUser]);

  return (
    <div>
      <h3>Your Inventory</h3>
      {foodItems.length === 0 ? (
        <p>No items yet.</p>
      ) : (
        <table style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead>
            <tr>
              <th>Name</th>
              <th>Expiry</th>
              <th>Quantity</th>
              <th>Category</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {foodItems.map(item => (
              <tr key={item.fooditemid}>
                <td>{item.name}</td>
                <td>{item.expirydate}</td>
                <td>{item.quantity}</td>
                <td>{item.foodcategory || ""}</td>
                <td>
                  <button onClick={() => deleteItem(item.fooditemid)} style={{ marginRight: "5px" }}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
