import React, { Component } from 'react';
import Communication from '../communication/Communication';
import Donations from '../donation_coordination/Donations';
import Waste from '../waste_analyses/WasteAnalyses';
import Notifications from '../notifications/Notification';
import ExpiryManager from '../expiry_manager/ExpiryManager';
import Recipes from '../recipe/Recipe';

// Import the new food components
import HomeDash from './HomeComponents/HomeDash.jsx';
import HomeForm from './HomeComponents/HomeForm.jsx';
import './HomeUser.css';

class HomeUser extends Component {
  state = {
    showForm: false,
    refreshFlag: false,
    currentUser: this.props.currentUser // Assuming currentUser is passed as prop
  };

  toggleForm = () => this.setState({ showForm: !this.state.showForm });

  triggerRefresh = () => this.setState({ refreshFlag: !this.state.refreshFlag });

  render() {
    const { showForm, refreshFlag, currentUser } = this.state;

    return (
      <div style={{ padding: '20px' }}>
        <h2 style={{ textAlign: 'center' }}>Home User Dashboard</h2>

        {/* Existing dashboard modules */}
       

        {/* Food Inventory Section */}
        <div style={{ marginTop: '40px' }}>
          {showForm ? (
            <HomeForm
              currentUser={currentUser}
              onBack={this.toggleForm}
              onAdded={this.triggerRefresh}
            />
          ) : (
            <HomeDash
              currentUser={currentUser}
              onAddNew={this.toggleForm}
              refreshFlag={refreshFlag}
            />
          )}
        </div>
      </div>
    );
  }
}

export default HomeUser;
