/* Author: Bethlehem Shimelis
   Event: Sprint 1: Manually Input Food Items with Expiry dates
   LatestUpdate: Established User Login and Registration
   parameters: user input
   Description: Processes User requests for Login and Registration
   Returns: posts to the API backend*/

import { useState } from "react";
import axios from "axios";

export default function LoginForm({ onLogin }) {
  const [form, setForm] = useState({ email: "", password: "" });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(
        "https://foodsave-backend.onrender.com/api/auth/login",
        form
      );

      setSuccess(res.data.message || "Login successful!");
      setError("");

      if (onLogin) onLogin(res.data.user); // send user object to App.js
    } catch (err) {
      console.error("Login error:", err);
      setError(err.response?.data?.error || "Invalid email or password");
      setSuccess("");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="form-card">
      <h1>FOODSAVE HUB</h1>

      <input
        type="email"
        name="email"
        placeholder="Email"
        value={form.email}
        onChange={handleChange}
        required
      />
      <input
        type="password"
        name="password"
        placeholder="Password"
        value={form.password}
        onChange={handleChange}
        required
      />

      {error && <p className="error-msg">{error}</p>}
      {success && <p className="success-msg">{success}</p>}

      <button type="submit">Login</button>
    </form>
  );
}
