import React, { Component } from 'react';
import './Notifications.css';


class Notification extends Component {
  constructor(props) {
    super(props);
    this.state = {
      notifications: [], // start with an empty array
      loading: true,
      error: null
    };
  }

  componentDidMount() {
  this.fetchNotifications(); // first fetch

  // fetch every 30 minutes
  this.interval = setInterval(() => {
    this.fetchNotifications();
  }, 30 * 60 * 1000); // 30 minutes
}

componentWillUnmount() {
  clearInterval(this.interval); // clean up interval
}

fetchNotifications = () => {
  fetch('http://localhost:5000/notifications')
    .then((res) => res.json())
    .then((data) => {
      if (Array.isArray(data)) {
        this.setState({ notifications: data, loading: false });
      } else {
        this.setState({ notifications: [], loading: false });
      }
    })
    .catch((err) => this.setState({ error: 'Failed to fetch notifications', loading: false }));
}


  render() {
  const { notifications, loading, error } = this.state;

  if (loading) return <p style={{ textAlign: 'center', marginTop: '50px' }}>Loading...</p>;
  if (error) return <p style={{ textAlign: 'center', marginTop: '50px', color: 'red' }}>{error}</p>;

  return (
    <div className="panel">
      <div className="panel-header">
        <span>Notifications</span>
        <button className="clear-btn">Clear All</button>
      </div>
      <div className="notifications">
        {notifications.length > 0 ? (
          notifications.map((note) => (
            <div className="notification" key={note.id}>
             <img 
          src={note.image_url}   // üëà this comes from DB
          alt={note.name} 
          style={{ width: "36px", height: "36px", borderRadius: "50%" }}
        />
              <div className="notification-content">
                <p>{note.message}</p>

                <span className="time">‚è≥ Soon</span>
              </div>
            </div>
          ))
        ) : (
          <p style={{ textAlign: 'center', padding: '10px' }}>No notifications üéâ</p>
        )}
      </div>
    </div>
  );
}

}

export default Notification;
