import { useState, useEffect } from "react";
import axios from "axios";
import "../HomeUser.css"; // make sure your CSS matches the form styling

export default function HomeDash({ currentUser, onAddNew }) {
  const [foodItems, setFoodItems] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [editForm, setEditForm] = useState({});

  const foodCategories = [
    "Produce","Dairy & Eggs","Meat","Seafood","Bakery","Grains",
    "Frozen Foods","Beverages","Snacks","Prepared Meals","Other"
  ];

  const units = ["ml", "L", "g", "kg"];

  const fetchFoodItems = async () => {
    try {
      const res = await axios.get(
        `https://foodsave-backend.onrender.com/api/users/fooditems?email=${currentUser.email}`
      );
      setFoodItems(res.data.foodItems);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchFoodItems();
  }, []);

  const startEdit = (item) => {
    setEditingId(item.fooditemid);
    setEditForm({
      ...item,
      Measure_per_Unit: item.Measure_per_Unit || 1,
      Unit: item.Unit || "g",
    });
  };

  const cancelEdit = () => {
    setEditingId(null);
    setEditForm({});
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditForm({ ...editForm, [name]: value });
  };

  const saveEdit = async () => {
    try {
      await axios.put(
        `https://foodsave-backend.onrender.com/api/users/fooditems/${editingId}`,
        {
          ...editForm,
          stakeholderID: currentUser.stakeholderID,
        }
      );
      setEditingId(null);
      fetchFoodItems();
    } catch (err) {
      console.error(err);
      alert("Failed to save changes");
    }
  };

  return (
    <div className="table-form-container">
      <h1 className="table-title">Food Inventory</h1>
      <table className="form-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Expiry</th>
            <th>Qty</th>
            <th>Category</th>
            <th>Measure / Unit</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {foodItems.map((item) => (
            <tr
              key={item.fooditemid}
              style={{
                backgroundColor:
                  new Date(item.expirydate) - new Date() < 2 * 24 * 60 * 60 * 1000
                    ? "#f8d7da"
                    : "transparent",
              }}
            >
              <td>
                {editingId === item.fooditemid ? (
                  <input
                    name="name"
                    value={editForm.name}
                    onChange={handleEditChange}
                  />
                ) : (
                  item.name
                )}
              </td>
              <td>
                {editingId === item.fooditemid ? (
                  <input
                    type="date"
                    name="expirydate"
                    value={editForm.expirydate.slice(0,10)}
                    onChange={handleEditChange}
                  />
                ) : (
                  new Date(item.expirydate).toLocaleDateString()
                )}
              </td>
              <td>
                {editingId === item.fooditemid ? (
                  <input
                    type="number"
                    name="quantity"
                    value={editForm.quantity}
                    onChange={handleEditChange}
                    min="1"
                  />
                ) : (
                  item.quantity
                )}
              </td>
              <td>
                {editingId === item.fooditemid ? (
                  <select
                    name="foodcategory"
                    value={editForm.foodcategory}
                    onChange={handleEditChange}
                  >
                    {foodCategories.map((cat) => (
                      <option key={cat} value={cat}>{cat}</option>
                    ))}
                  </select>
                ) : (
                  item.foodcategory
                )}
              </td>
              <td>
                {editingId === item.fooditemid ? (
                  <div style={{ display: "flex", gap: "5px" }}>
                    <input
                      type="number"
                      name="Measure_per_Unit"
                      value={editForm.Measure_per_Unit}
                      onChange={handleEditChange}
                      min="1"
                    />
                    <select
                      name="Unit"
                      value={editForm.Unit}
                      onChange={handleEditChange}
                    >
                      {units.map((u) => (
                        <option key={u} value={u}>{u}</option>
                      ))}
                    </select>
                  </div>
                ) : (
                  `${item.Measure_per_Unit} ${item.Unit}`
                )}
              </td>
              <td>
                {editingId === item.fooditemid ? (
                  <>
                    <button className="btn-orange" onClick={saveEdit}>
                      Save
                    </button>
                    <button className="btn-cancel" onClick={cancelEdit}>
                      Cancel
                    </button>
                  </>
                ) : (
                  <button className="btn-edit" onClick={() => startEdit(item)}>
                    Edit
                  </button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="add-btn-container">
        <button className="btn-green" onClick={onAddNew}>
          Add Food Item
        </button>
      </div>
    </div>
  );
}
