// src/users/HomeDash.jsx
import { useEffect, useState } from "react";
import axios from "axios";
import "../HomeUser.css"; // ensure your CSS is imported

export default function HomeDash({ currentUser, onAddNew, refreshFlag }) {
  const [foodItems, setFoodItems] = useState([]);

  const fetchFoodItems = async () => {
    if (!currentUser) return;
    try {
      // Use your deployed backend URL
      const res = await axios.get(
        `https://foodsave-backend.onrender.com/api/users/fooditems?stakeholderID=${currentUser.stakeholderID}`
      );
      setFoodItems(res.data.foodItems);
    } catch (err) {
      console.error("Failed to fetch food items:", err);
    }
  };

  useEffect(() => {
    fetchFoodItems();
  }, [currentUser, refreshFlag]);

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this item?")) return;
    try {
      await axios.delete(
        `https://foodsave-backend.onrender.com/api/users/fooditems/${id}`
      );
      fetchFoodItems();
    } catch (err) {
      console.error("Failed to delete food item:", err);
    }
  };

  return (
    <div className="dashboard-container">
      <div className="form-card" style={{ maxWidth: "700px", width: "100%" }}>
        <h1 style={{ textAlign: "center" }}>Your Food Inventory</h1>
        <button
          type="button"
          onClick={onAddNew}
          style={{
            marginBottom: "15px",
            backgroundColor: "#16a34a",
            color: "#fff",
            width: "fit-content",
            padding: "10px 20px",
            alignSelf: "center",
            borderRadius: "8px",
            cursor: "pointer",
          }}
        >
          Add New Food Item
        </button>

        <div className="table-container">
          {foodItems.map((item) => (
            <div key={item.fooditemid} className="food-row">
              <span>{item.name}</span>
              <span>{new Date(item.expirydate).toLocaleDateString()}</span>
              <span>{item.quantity}</span>
              <span>{item.foodcategory || ""}</span>
              <div className="row-actions">
                <button onClick={() => handleDelete(item.fooditemid)}>Delete</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
