import React, { Component } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';

import Login from './auth/LoginSignup';
import HomeUser from './users/HomeUser';
import Business from './users/Business';
import Charity from './users/Charity';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { loggedIn: false };
  }

  handleLogin = () => this.setState({ loggedIn: true });
  handleLogout = () => this.setState({ loggedIn: false });

  renderNavbar() {
    return (
      <nav style={{ padding: 10, borderBottom: '1px solid #ddd' }}>
        <Link to="/">üè† Home User</Link> |{' '}
        <Link to="/business">üíº Business</Link> |{' '}
        <Link to="/charity">ü§ù Charity</Link> |{' '}
        <button
          onClick={this.handleLogout}
          style={{
            marginLeft: 10,
            padding: '4px 8px',
            backgroundColor: '#eee',
            border: '1px solid #ccc',
            cursor: 'pointer',
          }}
        >
          Log out
        </button>
      </nav>
    );
  }

  render() {
    const { loggedIn } = this.state;
    return (
      <Router>
        {!loggedIn ? (
          <Login onLogin={this.handleLogin} />
        ) : (
          <div>
            {this.renderNavbar()}
            <Routes>
              <Route path="/" element={<HomeUser />} />
              <Route path="/business" element={<Business />} />
              <Route path="/charity" element={<Charity />} />
            </Routes>
          </div>
        )}
      </Router>
    );
  }
}

export default App;
