import React, { useState, useEffect, useMemo } from "react";
import axios from "axios";
import "./Profile.css";

const API_BACKEND = process.env.REACT_APP_API_BACKEND;

const regions = [
  "Durban, KwaZulu-Natal","Cape Town, Western Cape","Pretoria, Gauteng",
  "Johannesburg, Gauteng","Port Elizabeth (Gqeberha), Eastern Cape",
  "Bloemfontein, Free State","Nelspruit (Mbombela), Mpumalanga",
  "Kimberley, Northern Cape","Potchefstroom, North West","Polokwane, Limpopo",
  "George, Western Cape","East London, Eastern Cape","Welkom, Free State",
  "Mthatha, Eastern Cape","Stellenbosch, Western Cape","Pietermaritzburg, KwaZulu-Natal"
];

export default function User({ currentUser, onLogout }) {
  const [userData, setUserData] = useState(null);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({});
  const [message, setMessage] = useState("");
  const [regionSearch, setRegionSearch] = useState("");
  const [showDropdown, setShowDropdown] = useState(false);
  const [changePasswordFlow, setChangePasswordFlow] = useState(false);
  const [otpRequested, setOtpRequested] = useState(false);
  const [otp, setOtp] = useState("");
  const [resendCooldown, setResendCooldown] = useState(0);

  useEffect(() => {
    if (currentUser) {
      setUserData(currentUser);
      setFormData({
        name: currentUser.name,
        region: currentUser.region,
        capacity: currentUser.capacity || "",
        currentPassword: "",
        newPassword: "",
        confirmPassword: "",
        push_notifications: currentUser.push_notifications === 1
      });
      setRegionSearch(currentUser.region);
    }
  }, [currentUser]);

  useEffect(() => {
    if (resendCooldown <= 0) return;
    const timer = setInterval(() => setResendCooldown(prev => prev - 1), 1000);
    return () => clearInterval(timer);
  }, [resendCooldown]);

  const filteredRegions = useMemo(() => {
    return regions.filter(r => r.toLowerCase().includes(regionSearch.toLowerCase()));
  }, [regionSearch]);

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleRegionSelect = (region) => {
    setFormData(prev => ({ ...prev, region }));
    setRegionSearch(region);
    setShowDropdown(false);
  };

  const toggleEditing = () => {
    setEditing(!editing);
    setMessage("");
    setChangePasswordFlow(false);
    setOtpRequested(false);
    setOtp("");
  };

  const requestOtp = async () => {
    try {
      await axios.post(`${API_BACKEND}/auth/request-otp`, { email: userData.email });
      setOtpRequested(true);
      setResendCooldown(30);
      setMessage("OTP sent! Check your email.");
    } catch (err) {
      console.error(err);
      setMessage(err.response?.data?.error || "Failed to request OTP");
    }
  };

  const verifyOtp = async () => {
    try {
      await axios.post(`${API_BACKEND}/auth/verify-otp`, { email: userData.email, otp });
      setMessage("OTP verified! Enter new password below.");
    } catch (err) {
      console.error(err);
      setMessage(err.response?.data?.error || "OTP verification failed");
    }
  };

  const validatePassword = (password) => {
    const strongRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d).{8,}$/;
    return strongRegex.test(password);
  };

  const handleSave = async () => {
    setMessage("");

    if (changePasswordFlow) {
      if (!otpRequested) return setMessage("Please request OTP first.");
      if (!otp) return setMessage("Enter OTP to continue.");
      if (!formData.newPassword || !formData.confirmPassword)
        return setMessage("Enter new password and confirm it.");
      if (formData.newPassword !== formData.confirmPassword)
        return setMessage("Passwords do not match.");
      if (!validatePassword(formData.newPassword))
        return setMessage("Password must be 8+ chars, include uppercase, lowercase, and a number.");
    } else {
      if (!formData.currentPassword) return setMessage("Enter your current password to update profile.");
    }

    try {
      const payload = {
        email: userData.email,
        currentPassword: formData.currentPassword,
        name: formData.name,
        region: formData.region,
        capacity: userData.stakeholderID?.startsWith("c") ? Number(formData.capacity) : undefined,
        newPassword: changePasswordFlow ? formData.newPassword : undefined,
        push_notifications: formData.push_notifications ? 1 : 0
      };

      const res = await axios.put(`${API_BACKEND}/profile/update`, payload);
      setUserData(res.data.user);
      setEditing(false);
      setChangePasswordFlow(false);
      setOtpRequested(false);
      setOtp("");
      setMessage("Profile updated successfully!");
      setFormData(prev => ({ ...prev, currentPassword: "", newPassword: "", confirmPassword: "" }));
    } catch (err) {
      console.error(err);
      setMessage(err.response?.data?.error || "Failed to update profile.");
    }
  };

  if (!userData) return <p>Loading...</p>;

  return (
    <div className="profile-page">
      <div className="profile-card">
        <h2>User Profile</h2>

        <div className="profile-row">
          <label>Email:</label>
          <span>{userData.email}</span>
        </div>

        <div className="profile-row">
          <label>Name:</label>
          {editing ? (
            <input type="text" name="name" value={formData.name} onChange={handleChange} className="input-field" />
          ) : <span>{userData.name}</span>}
        </div>

        <div className="profile-row">
          <label>Region:</label>
          {editing ? (
            <div className="region-search-container">
              <input
                type="text"
                placeholder="Search Region..."
                value={regionSearch}
                onChange={(e) => { setRegionSearch(e.target.value); setFormData(prev => ({ ...prev, region: e.target.value })); setShowDropdown(true); }}
                onFocus={() => setShowDropdown(true)}
                onBlur={() => setTimeout(() => setShowDropdown(false), 150)}
                className="input-field"
              />
              {showDropdown && filteredRegions.length > 0 && (
                <div className="region-dropdown">
                  {filteredRegions.map((r, i) => (
                    <div key={i} className="region-option" onClick={() => handleRegionSelect(r)}>{r}</div>
                  ))}
                </div>
              )}
            </div>
          ) : <span>{userData.region}</span>}
        </div>

        {userData.stakeholderID?.startsWith("c") && (
          <div className="profile-row">
            <label>Capacity:</label>
            {editing ? (
              <input type="number" name="capacity" value={formData.capacity} onChange={handleChange} className="input-field" />
            ) : <span>{userData.capacity}</span>}
          </div>
        )}

        <div className="profile-row">
          <label>Push Notifications:</label>
          {editing && (
            <input type="checkbox" name="push_notifications" checked={formData.push_notifications} onChange={(e) => setFormData(prev => ({ ...prev, push_notifications: e.target.checked }))} />
          )}
          {!editing && <span>{userData.push_notifications ? "Enabled" : "Disabled"}</span>}
        </div>

        {editing && (
          <>
            {!changePasswordFlow && (
              <button className="btn-toggle" onClick={() => setChangePasswordFlow(true)}>
                Would you like to change your password?
              </button>
            )}
            {changePasswordFlow && (
              <>
                {!otpRequested && (
                  <button className="btn-toggle" onClick={requestOtp}>
                    Request OTP to change password
                  </button>
                )}
                {otpRequested && (
                  <div className="otp-section">
                    <input type="text" placeholder="Enter OTP" value={otp} onChange={e => setOtp(e.target.value)} className="input-field" />
                    <button onClick={verifyOtp}>Verify OTP</button>
                  </div>
                )}
                <input type="password" name="newPassword" placeholder="New Password" value={formData.newPassword} onChange={handleChange} className="input-field" />
                <input type="password" name="confirmPassword" placeholder="Confirm New Password" value={formData.confirmPassword} onChange={handleChange} className="input-field" />
              </>
            )}
            <input type="password" name="currentPassword" placeholder="Current Password" value={formData.currentPassword} onChange={handleChange} className="input-field" />
          </>
        )}

        <div className="profile-actions">
          {editing ? (
            <>
              <button className="btn btn-save" onClick={handleSave}>Save</button>
              <button className="btn btn-cancel" onClick={toggleEditing}>Cancel</button>
            </>
          ) : (
            <button className="btn btn-edit" onClick={toggleEditing}>Edit Profile</button>
          )}
        </div>

        {message && <p className="profile-message">{message}</p>}
      </div>
    </div>
  );
}
