import React, { useState, useEffect, useMemo } from "react";
import axios from "axios";
import "./HomeUser.css";

const API_BACKEND = process.env.REACT_APP_API_BACKEND;

const regions = [ 
  "Durban, KwaZulu-Natal",
  "Cape Town, Western Cape",
  "Pretoria, Gauteng",
  "Johannesburg, Gauteng",
  "Port Elizabeth (Gqeberha), Eastern Cape",
  "Bloemfontein, Free State",
  "Nelspruit (Mbombela), Mpumalanga",
  "Kimberley, Northern Cape",
  "Potchefstroom, North West",
  "Polokwane, Limpopo",
  "George, Western Cape",
  "East London, Eastern Cape",
  "Welkom, Free State",
  "Mthatha, Eastern Cape",
  "Stellenbosch, Western Cape",
  "Pietermaritzburg, KwaZulu-Natal"
];

export default function User({ currentUser, onLogout }) {
  const [userData, setUserData] = useState(null);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({});
  const [message, setMessage] = useState("");
  const [regionSearch, setRegionSearch] = useState("");
  const [showDropdown, setShowDropdown] = useState(false);

  useEffect(() => {
    if (currentUser) {
      setUserData(currentUser);
      setFormData({
        name: currentUser.name,
        region: currentUser.region,
        capacity: currentUser.capacity || "",
        password: "",
        confirmPassword: "",
        push_notifications: currentUser.push_notifications
      });
      setRegionSearch(currentUser.region);
    }
  }, [currentUser]);

  const filteredRegions = useMemo(() => {
    return regions.filter(r =>
      r.toLowerCase().includes(regionSearch.toLowerCase())
    );
  }, [regionSearch]);

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleRegionSelect = (region) => {
    setFormData(prev => ({ ...prev, region }));
    setRegionSearch(region);
    setShowDropdown(false);
  };

  const handleEditToggle = () => {
    setEditing(!editing);
    setMessage("");
  };

  const validatePassword = (password) => {
    const regex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
    return regex.test(password);
  };

  const handleSave = async () => {
    if (formData.password) {
      if (!validatePassword(formData.password)) {
        setMessage("Password must be at least 8 chars, include uppercase, lowercase, number, and special char.");
        return;
      }
      if (formData.password !== formData.confirmPassword) {
        setMessage("Passwords do not match!");
        return;
      }
    }

    try {
      const payload = {
        name: formData.name,
        region: formData.region,
        push_notifications: formData.push_notifications
      };
      if (formData.password) payload.password = formData.password;
      if (userData.stakeholderID?.startsWith("c")) {
        payload.capacity = Number(formData.capacity);
      }

      const res = await axios.put(`${API_BACKEND}/api/users/update/${userData.email}`, payload);
      setUserData(res.data.user);
      setEditing(false);
      setMessage("Profile updated successfully!");
    } catch (err) {
      console.error(err);
      setMessage(err.response?.data?.error || "Failed to update profile.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("loggedInUser");
    if (onLogout) onLogout();
    window.location.href = "/"; // redirect to login or home
  };

  if (!userData) return <p>Loading...</p>;

  return (
    <div className="profile-page">
      <div className="profile-card">
        <h2>User Profile</h2>

        <div className="profile-row">
          <label>Email:</label>
          <span>{userData.email}</span>
        </div>

        <div className="profile-row">
          <label>Name:</label>
          {editing ? (
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              className="input-field"
            />
          ) : <span>{userData.name}</span>}
        </div>

        <div className="profile-row">
          <label>Region:</label>
          {editing ? (
            <div className="region-search-container">
              <input
                type="text"
                placeholder="Search Region..."
                value={regionSearch}
                onChange={(e) => {
                  setRegionSearch(e.target.value);
                  setFormData(prev => ({ ...prev, region: e.target.value }));
                  setShowDropdown(true);
                }}
                onFocus={() => setShowDropdown(true)}
                onBlur={() => setTimeout(() => setShowDropdown(false), 150)}
                className="input-field"
              />
              {showDropdown && filteredRegions.length > 0 && (
                <div className="region-dropdown">
                  {filteredRegions.map((r, i) => (
                    <div
                      key={i}
                      className="region-option"
                      onClick={() => handleRegionSelect(r)}
                    >
                      {r}
                    </div>
                  ))}
                </div>
              )}
            </div>
          ) : <span>{userData.region}</span>}
        </div>

        {userData.stakeholderID?.startsWith("c") && (
          <div className="profile-row">
            <label>Capacity:</label>
            {editing ? (
              <input
                type="number"
                name="capacity"
                value={formData.capacity}
                onChange={handleChange}
                className="input-field"
              />
            ) : <span>{userData.capacity}</span>}
          </div>
        )}

        <div className="profile-row">
          <label>Push Notifications:</label>
          {editing ? (
            <label className="switch">
              <input 
                type="checkbox" 
                checked={formData.push_notifications !== 0} 
                onChange={(e) => setFormData(prev => ({ ...prev, push_notifications: e.target.checked ? 1 : 0 }))}
              />
              <span className="slider round"></span>
            </label>
          ) : (
            <span>{userData.push_notifications ? "Enabled" : "Disabled"}</span>
          )}
        </div>

        {editing && (
          <>
            <div className="profile-row">
              <label>New Password:</label>
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                className="input-field"
              />
            </div>
            <div className="profile-row">
              <label>Confirm Password:</label>
              <input
                type="password"
                name="confirmPassword"
                value={formData.confirmPassword}
                onChange={handleChange}
                className="input-field"
              />
            </div>
          </>
        )}

        <div className="profile-actions">
          {editing ? (
            <>
              <button className="btn btn-save" onClick={handleSave}>Save</button>
              <button className="btn btn-cancel" onClick={handleEditToggle}>Cancel</button>
            </>
          ) : (
            <button className="btn btn-edit" onClick={handleEditToggle}>Edit Profile</button>
          )}
          <button className="btn btn-logout" onClick={handleLogout}>Logout</button>
        </div>

        {message && <p className="profile-message">{message}</p>}
      </div>
    </div>
  );
}
