import { useState } from "react";
import axios from "axios";

export default function HomeForm({ currentUser, onBack, onAdded }) {
  const [form, setForm] = useState({
    name: "",
    expiryDate: "",
    quantity: 1,
    foodCategory: ""
  });
  const [error, setError] = useState("");

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    // Validation
    if (!form.name) return setError("Name is required");
    if (!form.expiryDate || new Date(form.expiryDate) <= new Date())
      return setError("Expiry date must be in the future");
    if (form.quantity <= 0) return setError("Quantity must be at least 1");

    try {
      await axios.post("/users/fooditems", {
        ...form,
        stakeholderID: currentUser.stakeholderID
      });
      onAdded();  // Refresh dashboard
      onBack();   // Go back to dashboard
    } catch (err) {
      console.error(err);
      setError("Failed to add food item");
    }
  };

  return (
    <div className="form-card">
      <h1>Add Food Item</h1>
      {error && <div className="error-msg">{error}</div>}

      <input
        type="text"
        name="name"
        placeholder="Food Name"
        value={form.name}
        onChange={handleChange}
      />
      <input
        type="date"
        name="expiryDate"
        value={form.expiryDate}
        onChange={handleChange}
      />
      <input
        type="number"
        name="quantity"
        placeholder="Quantity"
        min="1"
        value={form.quantity}
        onChange={handleChange}
      />
      <select name="foodCategory" value={form.foodCategory} onChange={handleChange}>
        <option value="">Select Category</option>
        <option value="Fruit">Fruit</option>
        <option value="Vegetable">Vegetable</option>
        <option value="Dairy">Dairy</option>
        <option value="Grain">Grain</option>
        <option value="Other">Other</option>
      </select>

      <button onClick={handleSubmit}>Add Item</button>
      <button type="button" className="toggle-link" onClick={onBack}>Back</button>
    </div>
  );
}
