/* Author: Bethlehem Shimelis
   Event: Sprint 1: Manually Input Food Items with Expiry dates
   LatestUpdate: Added localStorage for logged-in user, improved error handling
   Parameters: 
      goToRegister (function) - callback to switch to registration form
      onLogin (function) - callback after successful login
   Description: Handles user login, posts credentials to API backend, stores user info
   Returns: Updates success/error messages, invokes onLogin callback, stores user in localStorage
*/

import { useState } from "react";
import axios from "axios";

const API_BACKEND = process.env.REACT_APP_API_BACKEND;

/* Author: Bethlehem Shimelis
   Event: Login Form Component
   LatestUpdate: Handles form state and submission
   Description: Renders login form, manages input state, sends POST request to backend
   Returns: JSX element for login form
*/
export default function LoginForm({ goToRegister, onLogin }) {
  const [form, setForm] = useState({ email: "", password: "" }); // stores form input values
  const [error, setError] = useState(""); // stores error messages
  const [success, setSuccess] = useState(""); // stores success messages

  /* Author: Bethlehem Shimelis
     Event: Handle Input Change
     Description: Updates form state when user types in inputs
     Returns: Updated form state
  */
  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  /* Author: Bethlehem Shimelis
     Event: Handle Form Submission
     Description: Sends login request to backend, handles success/error, stores user in localStorage
     Returns: Updates success/error state and calls onLogin callback
  */
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(`${API_BACKEND}/api/auth/login`, form);

      setSuccess(res.data.message || "Login successful!");
      setError("");

      if (onLogin) onLogin(res.data.user);

      // Save logged-in user locally
      localStorage.setItem("loggedInUser", JSON.stringify(res.data.user));
    } catch (err) {
      console.error("Login error:", err);
      setError(err.response?.data?.error || "Invalid email or password");
      setSuccess("");
    }
  };

  // UI Render ---
  return (
    <form onSubmit={handleSubmit} className="form-card">
      <h1>FOODSAVE HUB</h1>

      <input
        type="email"
        name="email"
        placeholder="Email"
        value={form.email}
        onChange={handleChange}
        required
      />
      <input
        type="password"
        name="password"
        placeholder="Password"
        value={form.password}
        onChange={handleChange}
        required
      />

      {error && <p className="error-msg">{error}</p>}
      {success && <p className="success-msg">{success}</p>}

      <button type="submit">Login</button>
      <button type="button" className="toggle-link" onClick={goToRegister}>
        Don't have an account? Sign Up
      </button>
    </form>
  );
}
