import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function RegisterForm({ goToLogin }) {
  const [form, setForm] = useState({
    accountType: "Household/Individual",
    name: "",
    email: "",
    province: "Durban, Kwa-Zulu Natal",
    password: "",
  });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
  e.preventDefault();
  try {
    const res = await axios.post("https://foodsave-backend.onrender.com/api/auth/login", form);
    localStorage.setItem("token", res.data.token); // optional
    setSuccess(res.data.message);
    setError("");
    // call the parent App.js onLogin function
    if (onLogin) onLogin();
  } catch (err) {
    setError(err.response?.data?.message || "Invalid email or password");
    setSuccess("");
  }
};


  return (
    <form onSubmit={handleSubmit} className="form-card">
      <h1>FOODSAVE HUB</h1>

      <select name="accountType" value={form.accountType} onChange={handleChange}>
        <option>Household/Individual</option>
        <option>Business/Corporate</option>
        <option>Charity/Foodbank</option>
      </select>

      <input type="text" name="name" placeholder="Name" value={form.name} onChange={handleChange} required />
      <input type="email" name="email" placeholder="Email" value={form.email} onChange={handleChange} required />
      <input type="password" name="password" placeholder="Password" value={form.password} onChange={handleChange} required />

      <select name="province" value={form.province} onChange={handleChange}>
        <option>Durban, Kwa-Zulu Natal</option>
        <option>Capetown, Western Cape</option>
        <option>Pretoria, Gauteng</option>
      </select>

      {error && <p className="error-msg">{error}</p>}
      {success && <p className="success-msg">{success}</p>}

      <button type="submit">Create Account</button>
      <button type="button" className="toggle-link" onClick={goToLogin}>
        Already have an account? Login
      </button>
    </form>
  );
}
