/* Author: Bethlehem Shimelis
   Event: FoodSave Hub 2025
   Description: Home dashboard for household users - view, edit, delete food items */

import { useEffect, useState } from "react";
import axios from "axios";
import "./HomeDash.css";

export default function HomeDash({ currentUser, goToForm }) {
  const [foodItems, setFoodItems] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [editedItem, setEditedItem] = useState({});

  // fetch all food items for the current user
  const fetchFoodItems = async () => {
    try {
      const res = await axios.get(
        `https://foodsave-backend.onrender.com/api/users/fooditems?email=${currentUser.email}`
      );
      setFoodItems(res.data.foodItems);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchFoodItems();
  }, []);

  // helper to determine row class based on expiry
  const getExpiryClass = (expirydate) => {
    const today = new Date();
    const expiry = new Date(expirydate);
    const diffDays = Math.ceil((expiry - today) / (1000 * 60 * 60 * 24));

    if (diffDays < 0) return "expired";
    if (diffDays <= 2) return "expiring-soon";
    return "";
  };

  const handleEditClick = (item) => {
    setEditingId(item.fooditemid);
    setEditedItem({ ...item });
  };

  const handleCancelEdit = () => {
    setEditingId(null);
    setEditedItem({});
  };

  const handleInputChange = (e) => {
    setEditedItem({ ...editedItem, [e.target.name]: e.target.value });
  };

  const handleSave = async (id) => {
    try {
      await axios.put(
        `https://foodsave-backend.onrender.com/api/users/fooditems/${id}`,
        editedItem
      );
      setEditingId(null);
      setEditedItem({});
      fetchFoodItems();
    } catch (err) {
      console.error("Failed to save edit:", err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(
        `https://foodsave-backend.onrender.com/api/users/fooditems/${id}?email=${currentUser.email}`
      );
      fetchFoodItems();
    } catch (err) {
      console.error("Failed to delete item:", err);
    }
  };

  return (
    <div className="table-container">
      <h1 className="table-title">Inventory</h1>

      <table className="food-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Expiry Date</th>
            <th>Quantity</th>
            <th>Category</th>
            <th>Measure</th>
            <th>Unit</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {foodItems
            .sort(
              (a, b) =>
                new Date(a.expirydate) - new Date(b.expirydate) // sort soonest first
            )
            .map((item) => (
              <tr
                key={item.fooditemid}
                className={getExpiryClass(item.expirydate)}
              >
                <td>
                  {editingId === item.fooditemid ? (
                    <input
                      name="name"
                      value={editedItem.name}
                      onChange={handleInputChange}
                    />
                  ) : (
                    item.name
                  )}
                </td>
                <td>
                  {editingId === item.fooditemid ? (
                    <input
                      type="date"
                      name="expirydate"
                      value={editedItem.expirydate.slice(0, 10)}
                      onChange={handleInputChange}
                    />
                  ) : (
                    new Date(item.expirydate).toLocaleDateString()
                  )}
                </td>
                <td>
                  {editingId === item.fooditemid ? (
                    <input
                      type="number"
                      name="quantity"
                      value={editedItem.quantity}
                      onChange={handleInputChange}
                    />
                  ) : (
                    item.quantity
                  )}
                </td>
                <td>
                  {editingId === item.fooditemid ? (
                    <input
                      name="foodcategory"
                      value={editedItem.foodcategory}
                      onChange={handleInputChange}
                    />
                  ) : (
                    item.foodcategory
                  )}
                </td>
                <td>
                  {editingId === item.fooditemid ? (
                    <input
                      type="number"
                      name="Measure_per_Unit"
                      value={editedItem.Measure_per_Unit}
                      onChange={handleInputChange}
                    />
                  ) : (
                    item.Measure_per_Unit
                  )}
                </td>
                <td>
                  {editingId === item.fooditemid ? (
                    <input
                      name="Unit"
                      value={editedItem.Unit}
                      onChange={handleInputChange}
                    />
                  ) : (
                    item.Unit
                  )}
                </td>
                <td>
                  {editingId === item.fooditemid ? (
                    <>
                      <button
                        className="btn btn-orange"
                        onClick={() => handleSave(item.fooditemid)}
                      >
                        Save
                      </button>
                      <button
                        className="btn btn-gray"
                        onClick={handleCancelEdit}
                      >
                        Cancel
                      </button>
                    </>
                  ) : (
                    <>
                      <button
                        className="btn btn-orange"
                        onClick={() => handleEditClick(item)}
                      >
                        Edit
                      </button>
                      <button
                        className="btn btn-red"
                        onClick={() => handleDelete(item.fooditemid)}
                      >
                        Delete
                      </button>
                    </>
                  )}
                </td>
              </tr>
            ))}
        </tbody>
      </table>

      <div className="add-btn-container">
        <button className="btn btn-green" onClick={goToForm}>
          Add Food Item
        </button>
      </div>
    </div>
  );
}
