// src/App.js
import React, { Component } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';

import Login from './auth/Login';
import Home from './users/Home';
import Business from './users/Business';
import Charity from './users/Charity';
import Recipes from './recipe/Recipes'; // Only needed for Home (general users)

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { loggedIn: false };
  }

  handleLogin = () => this.setState({ loggedIn: true });
  handleLogout = () => this.setState({ loggedIn: false });

  renderNavbar() {
    return (
      <nav style={{ padding: 10, borderBottom: '1px solid #ddd' }}>
        <Link to="/">Home</Link> |{' '}
        <Link to="/business">Business</Link> |{' '}
        <Link to="/charity">Charity</Link> |{' '}
        <Link to="/recipes">Recipes</Link> |{' '}
        <button onClick={this.handleLogout} style={{ marginLeft: 10 }}>
          Log out
        </button>
      </nav>
    );
  }

  render() {
    const { loggedIn } = this.state;
    return (
      <Router>
        {!loggedIn ? (
          <Login onLogin={this.handleLogin} />
        ) : (
          <div>
            {this.renderNavbar()}
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/business" element={<Business />} />
              <Route path="/charity" element={<Charity />} />
              <Route path="/recipes" element={<Recipes />} />
            </Routes>
          </div>
        )}
      </Router>
    );
  }
}

export default App;
