import { useEffect, useState } from "react";
import axios from "axios";

export default function HomeDash({ currentUser, onAddNew, refreshFlag }) {
  const [foodItems, setFoodItems] = useState([]);

  const fetchFoodItems = async () => {
    try {
      const res = await axios.get(
        `https://foodsave-backend.onrender.com/api/users/fooditems?stakeholderID=${currentUser.stakeholderID}`
      );
      setFoodItems(res.data.foodItems);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchFoodItems();
  }, [refreshFlag]);

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this item?")) return;
    try {
      await axios.delete(
        `https://foodsave-backend.onrender.com/api/users/fooditems/${id}`
      );
      fetchFoodItems();
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="home-wrapper">
      <div className="table-card">
        <h1>Your Food Inventory</h1>

        {foodItems.length === 0 ? (
          <p className="empty-msg">No items yet.</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Expiry Date</th>
                <th>Quantity</th>
                <th>Food Category</th>
                <th>Measure per Unit</th>
                <th>Unit</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {foodItems.map((item) => (
                <tr key={item.fooditemid}>
                  <td>{item.name}</td>
                  <td>{new Date(item.expirydate).toLocaleDateString()}</td>
                  <td>{item.quantity}</td>
                  <td>{item.foodcategory}</td>
                  <td>{item.measureperunit}</td>
                  <td>{item.unit}</td>
                  <td>
                    <button onClick={() => handleDelete(item.fooditemid)}>
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}

        <button className="bottom-action-button" onClick={onAddNew}>
          Add New Food Item
        </button>
      </div>
    </div>
  );
}
