import React, { Component } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';

// Pages
import Login from './login_signup/Login';
import Home from './users/Home';
import User from './users/User';
import Communication from './communication/Communication';
import Donations from './donation_coordination/Donations';
import Recipes from './recipe/Recipes';
import Waste from './waste_analyses/Waste';
import Notifications from './notification/Notifications';
import ExpiryManager from './expiryManager/ExpiryManager';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loggedIn: false,
    };
  }

  handleLogin = () => {
    this.setState({ loggedIn: true });
  };

  handleLogout = () => {
    this.setState({ loggedIn: false });
  };

  renderNavbar() {
    return (
      <nav style={{ padding: 10, borderBottom: '1px solid #ddd' }}>
        <Link to="/">Home</Link> |{' '}
        <Link to="/profile">Profile</Link> |{' '}
        <Link to="/communication">Communication</Link> |{' '}
        <Link to="/donations">Donations</Link> |{' '}
        <Link to="/recipes">Recipes</Link> |{' '}
        <Link to="/waste">Waste Analysis</Link> |{' '}
        <Link to="/notifications">Notifications</Link> |{' '}
        <Link to="/expiry">Expiry Manager</Link> |{' '}
        <button
          onClick={this.handleLogout}
          style={{
            marginLeft: 10,
            padding: '4px 8px',
            backgroundColor: '#eee',
            border: '1px solid #ccc',
            cursor: 'pointer',
          }}
        >
          Log out
        </button>
      </nav>
    );
  }

  render() {
    const { loggedIn } = this.state;

    return (
      <Router>
        {!loggedIn ? (
          <Login onLogin={this.handleLogin} />
        ) : (
          <div>
            {this.renderNavbar()}
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/profile" element={<User />} />
              <Route path="/communication" element={<Communication />} />
              <Route path="/donations" element={<Donations />} />
              <Route path="/recipes" element={<Recipes />} />
              <Route path="/waste" element={<Waste />} />
              <Route path="/notifications" element={<Notifications />} />
              <Route path="/expiry" element={<ExpiryManager />} />
            </Routes>
          </div>
        )}
      </Router>
    );
  }
}

export default App;
