import { useState, useEffect } from "react";
import axios from "axios";
import HomeForm from "./HomeForm";
import "./HomeUser.css";

export default function HomeDash({ currentUser }) {
  const [foodItems, setFoodItems] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // Fetch food items from backend
  const fetchFoodItems = async () => {
    if (!currentUser?.email) return;
    setLoading(true);
    setError("");

    try {
      const res = await axios.get(
        "https://foodsave-backend.onrender.com/api/users/fooditems",
        { params: { email: currentUser.email } } // send email for auth
      );
      setFoodItems(res.data.foodItems);
    } catch (err) {
      console.error(err);
      setError("Failed to load food items");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchFoodItems();
  }, [currentUser]);

  const handleRefresh = () => fetchFoodItems();

  if (!currentUser) return <p>Please log in to view your inventory.</p>;

  return (
    <div className="dashboard-container">
      <h1>{currentUser.name}'s Food Inventory</h1>

      {error && <p className="error-msg">{error}</p>}

      <button onClick={() => setShowForm(!showForm)}>
        {showForm ? "Close Add Form" : "Add Food Item"}
      </button>

      {showForm && (
        <HomeForm
          currentUser={currentUser}
          onClose={() => setShowForm(false)}
          onRefresh={handleRefresh}
        />
      )}

      {loading ? (
        <p>Loading food items...</p>
      ) : foodItems.length === 0 ? (
        <p>No food items found.</p>
      ) : (
        <table className="food-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Expiry Date</th>
              <th>Quantity</th>
              <th>Category</th>
              <th>Donation ID</th>
              <th>Measure per Unit</th>
              <th>Unit</th>
            </tr>
          </thead>
          <tbody>
            {foodItems.map((item) => (
              <tr key={item.fooditemid}>
                <td>{item.fooditemid}</td>
                <td>{item.name}</td>
                <td>{item.expirydate}</td>
                <td>{item.quantity}</td>
                <td>{item.foodcategory}</td>
                <td>{item.donationid || "-"}</td>
                <td>{item.Measure_per_Unit}</td>
                <td>{item.Unit}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
