import { useEffect, useState } from "react";
import axios from "axios";

export default function HomeDash({ currentUser, onAddNew, refreshFlag }) {
  const [foodItems, setFoodItems] = useState([]);
  const [editingItemId, setEditingItemId] = useState(null);
  const [editForm, setEditForm] = useState({});

  // Fetch all food items for current user
  const fetchFoodItems = async () => {
    try {
      const res = await axios.get(`/users/fooditems?stakeholderID=${currentUser.stakeholderID}`);
      setFoodItems(res.data.foodItems);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchFoodItems();
  }, [refreshFlag]);

  // Delete a food item
  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this item?")) return;
    try {
      await axios.delete(`/users/fooditems/${id}`);
      fetchFoodItems();
    } catch (err) {
      console.error(err);
    }
  };

  // Start editing a row
  const startEdit = (item) => {
    setEditingItemId(item.fooditemid);
    setEditForm({
      name: item.name,
      expiryDate: item.expirydate.split("T")[0], // format for input[type=date]
      quantity: item.quantity,
      foodCategory: item.foodcategory
    });
  };

  // Handle input changes in edit form
  const handleEditChange = (e) => {
    setEditForm({ ...editForm, [e.target.name]: e.target.value });
  };

  // Save the edited item
  const saveEdit = async () => {
    if (!editForm.name) return alert("Name is required");
    if (!editForm.expiryDate || new Date(editForm.expiryDate) <= new Date())
      return alert("Expiry date must be in the future");
    if (editForm.quantity <= 0) return alert("Quantity must be at least 1");

    try {
      await axios.put(`/users/fooditems/${editingItemId}`, editForm);
      setEditingItemId(null);
      fetchFoodItems();
    } catch (err) {
      console.error(err);
    }
  };

  // Cancel editing
  const cancelEdit = () => setEditingItemId(null);

  return (
    <div className="form-card">
      <h1>Your Food Inventory</h1>
      <button onClick={onAddNew}>Add New Food Item</button>

      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Expiry</th>
            <th>Quantity</th>
            <th>Category</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {foodItems.map((item) => (
            <tr key={item.fooditemid}>
              {editingItemId === item.fooditemid ? (
                <>
                  <td>
                    <input
                      type="text"
                      name="name"
                      value={editForm.name}
                      onChange={handleEditChange}
                    />
                  </td>
                  <td>
                    <input
                      type="date"
                      name="expiryDate"
                      value={editForm.expiryDate}
                      onChange={handleEditChange}
                    />
                  </td>
                  <td>
                    <input
                      type="number"
                      name="quantity"
                      min="1"
                      value={editForm.quantity}
                      onChange={handleEditChange}
                    />
                  </td>
                  <td>
                    <select
                      name="foodCategory"
                      value={editForm.foodCategory}
                      onChange={handleEditChange}
                    >
                      <option value="">Select</option>
                      <option value="Fruit">Fruit</option>
                      <option value="Vegetable">Vegetable</option>
                      <option value="Dairy">Dairy</option>
                      <option value="Grain">Grain</option>
                      <option value="Other">Other</option>
                    </select>
                  </td>
                  <td>
                    <button onClick={saveEdit}>Save</button>
                    <button onClick={cancelEdit}>Cancel</button>
                  </td>
                </>
              ) : (
                <>
                  <td>{item.name}</td>
                  <td>{new Date(item.expirydate).toLocaleDateString()}</td>
                  <td>{item.quantity}</td>
                  <td>{item.foodcategory}</td>
                  <td>
                    <button onClick={() => startEdit(item)}>Edit</button>
                    <button onClick={() => handleDelete(item.fooditemid)}>Delete</button>
                  </td>
                </>
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
