import { useState, useEffect } from "react";
import axios from "axios";
import HomeForm from "./HomeForm";

export default function HomeDash({ currentUser }) {
  const [foodItems, setFoodItems] = useState([]);
  const [showForm, setShowForm] = useState(false);

  // Fetch food items for the logged-in user
  const fetchFoodItems = async () => {
    try {
      const res = await axios.get(
        "https://foodsave-backend.onrender.com/api/users/fooditems",
        {
          params: { email: currentUser.email }, // send email for authentication
        }
      );
      setFoodItems(res.data.foodItems || []);
    } catch (err) {
      console.error("Failed to fetch food items:", err);
      setFoodItems([]);
    }
  };

  useEffect(() => {
    if (currentUser) fetchFoodItems();
  }, [currentUser]);

  return (
    <div className="dashboard-container">
      <h1>Your Food Inventory</h1>

      <button onClick={() => setShowForm(!showForm)}>
        {showForm ? "Back to Inventory" : "Add New Food Item"}
      </button>

      {showForm ? (
        <HomeForm
          currentUser={currentUser}
          onClose={() => setShowForm(false)}
          onRefresh={fetchFoodItems}
        />
      ) : (
        <table className="inventory-table">
          <thead>
            <tr>
              <th>Food Item ID</th>
              <th>Name</th>
              <th>Expiry Date</th>
              <th>Quantity</th>
              <th>Category</th>
              <th>Donation ID</th>
              <th>Measure per Unit</th>
              <th>Unit</th>
            </tr>
          </thead>
          <tbody>
            {foodItems.length === 0 ? (
              <tr>
                <td colSpan="8">No food items found.</td>
              </tr>
            ) : (
              foodItems.map((item) => (
                <tr key={item.fooditemid}>
                  <td>{item.fooditemid}</td>
                  <td>{item.name}</td>
                  <td>{item.expirydate}</td>
                  <td>{item.quantity}</td>
                  <td>{item.foodcategory}</td>
                  <td>{item.donationid || "-"}</td>
                  <td>{item.Measure_per_Unit}</td>
                  <td>{item.Unit}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      )}
    </div>
  );
}
