import React, { useState, useEffect, useMemo } from "react";
import axios from "axios";
import "./Profile.css";

const API_BACKEND = process.env.REACT_APP_API_BACKEND;

const regions = [
  "Durban, KwaZulu-Natal","Cape Town, Western Cape","Pretoria, Gauteng",
  "Johannesburg, Gauteng","Port Elizabeth (Gqeberha), Eastern Cape",
  "Bloemfontein, Free State","Nelspruit (Mbombela), Mpumalanga",
  "Kimberley, Northern Cape","Potchefstroom, North West","Polokwane, Limpopo",
  "George, Western Cape","East London, Eastern Cape","Welkom, Free State",
  "Mthatha, Eastern Cape","Stellenbosch, Western Cape","Pietermaritzburg, KwaZulu-Natal"
];

export default function Profile({ currentUser, onLogout }) {
  const [userData, setUserData] = useState(null);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({});
  const [message, setMessage] = useState("");
  const [regionSearch, setRegionSearch] = useState("");
  const [showDropdown, setShowDropdown] = useState(false);

  // Password change flow
  const [changePassword, setChangePassword] = useState(false);
  const [otp, setOtp] = useState("");
  const [otpRequested, setOtpRequested] = useState(false);
  const [otpVerified, setOtpVerified] = useState(false);
  const [resendCooldown, setResendCooldown] = useState(0);

  useEffect(() => {
    if (currentUser) {
      setUserData(currentUser);
      setFormData({
        name: currentUser.name,
        region: currentUser.region,
        capacity: currentUser.capacity || "",
        newPassword: "",
        confirmPassword: "",
        push_notifications: currentUser.push_notifications
      });
      setRegionSearch(currentUser.region);
    }
  }, [currentUser]);

  useEffect(() => {
    if (resendCooldown <= 0) return;
    const timer = setInterval(() => setResendCooldown(prev => prev - 1), 1000);
    return () => clearInterval(timer);
  }, [resendCooldown]);

  const filteredRegions = useMemo(() => {
    return regions.filter(r =>
      r.toLowerCase().includes(regionSearch.toLowerCase())
    );
  }, [regionSearch]);

  const handleChange = (e) => setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  const handleRegionSelect = (region) => {
    setFormData(prev => ({ ...prev, region }));
    setRegionSearch(region);
    setShowDropdown(false);
  };

  const handleEditToggle = () => {
    setEditing(!editing);
    setMessage("");
    setChangePassword(false);
    setOtpRequested(false);
    setOtpVerified(false);
    setOtp("");
    setFormData(prev => ({ ...prev, newPassword: "", confirmPassword: "" }));
  };

  // ===== OTP functions =====
  const requestOtp = async () => {
    try {
      await axios.post(`${API_BACKEND}/api/auth/request-otp`, { email: userData.email });
      setMessage("OTP sent! Check your email.");
      setOtpRequested(true);
      setResendCooldown(30);
    } catch (err) {
      console.error(err);
      setMessage(err.response?.data?.error || "Failed to request OTP");
    }
  };

  const verifyOtp = async () => {
    try {
      await axios.post(`${API_BACKEND}/api/auth/verify-otp`, { email: userData.email, otp });
      setOtpVerified(true);
      setMessage("OTP verified! Enter new password below.");
    } catch (err) {
      setMessage(err.response?.data?.error || "OTP verification failed");
    }
  };

  // ===== Save updates =====
  const handleSave = async () => {
    setMessage("");

    if (changePassword) {
      if (!otpVerified) {
        setMessage("Please verify OTP to change password.");
        return;
      }
      if (!formData.newPassword || formData.newPassword !== formData.confirmPassword) {
        setMessage("Passwords do not match or are empty.");
        return;
      }
      // Optional: frontend password strength checks
      if (formData.newPassword.length < 8 ||
          !/[A-Z]/.test(formData.newPassword) ||
          !/[a-z]/.test(formData.newPassword) ||
          !/[0-9]/.test(formData.newPassword)) {
        setMessage("Password must be 8+ chars, include upper, lower, and number.");
        return;
      }
    }

    try {
      const payload = {
        name: formData.name,
        region: formData.region,
        capacity: userData.stakeholderID?.startsWith("c") ? Number(formData.capacity) : undefined,
        push_notifications: formData.push_notifications ? 1 : 0
      };

      if (changePassword) payload.password = formData.newPassword;

      const res = await axios.put(`${API_BACKEND}/api/profile/update`, payload, { params: { email: userData.email } });

      setUserData(res.data.user);
      setEditing(false);
      setChangePassword(false);
      setOtpRequested(false);
      setOtpVerified(false);
      setOtp("");
      setFormData(prev => ({ ...prev, newPassword: "", confirmPassword: "" }));
      setMessage("Profile updated successfully!");
    } catch (err) {
      console.error(err);
      setMessage(err.response?.data?.error || "Failed to update profile");
    }
  };

  if (!userData) return <p>Loading...</p>;

  return (
    <div className="profile-page-updated">
      <div className="profile-card-updated">
        <h2>User Profile</h2>

        <div className="profile-row-updated">
          <label>Email:</label>
          <span>{userData.email}</span>
        </div>

        <div className="profile-row-updated">
          <label>Name:</label>
          {editing ? (
            <input type="text" name="name" value={formData.name} onChange={handleChange} className="input-field-updated" />
          ) : <span>{userData.name}</span>}
        </div>

        <div className="profile-row-updated">
          <label>Region:</label>
          {editing ? (
            <div className="region-search-container-updated">
              <input
                type="text"
                placeholder="Search Region..."
                value={regionSearch}
                onChange={(e) => { setRegionSearch(e.target.value); setFormData(prev => ({ ...prev, region: e.target.value })); setShowDropdown(true); }}
                onFocus={() => setShowDropdown(true)}
                onBlur={() => setTimeout(() => setShowDropdown(false), 150)}
                className="input-field-updated"
              />
              {showDropdown && filteredRegions.length > 0 && (
                <div className="region-dropdown-updated">
                  {filteredRegions.map((r, i) => (
                    <div key={i} className="region-option-updated" onClick={() => handleRegionSelect(r)}>{r}</div>
                  ))}
                </div>
              )}
            </div>
          ) : <span>{userData.region}</span>}
        </div>

        {userData.stakeholderID?.startsWith("c") && (
          <div className="profile-row-updated">
            <label>Capacity:</label>
            {editing ? (
              <input type="number" name="capacity" value={formData.capacity} onChange={handleChange} className="input-field-updated" />
            ) : <span>{userData.capacity}</span>}
          </div>
        )}

        {editing && (
          <>
            <div className="profile-row-updated">
              <label>
                <input type="checkbox" checked={changePassword} onChange={() => setChangePassword(prev => !prev)} /> Would you like to change your password?
              </label>
            </div>

            {changePassword && (
              <>
                <div className="profile-row-updated">
                  <label>Request OTP to change password:</label>
                  <button type="button" onClick={requestOtp} disabled={resendCooldown > 0}>
                    {resendCooldown > 0 ? `Resend OTP in ${resendCooldown}s` : "Send OTP"}
                  </button>
                </div>

                {otpRequested && !otpVerified && (
                  <div className="profile-row-updated">
                    <label>Enter OTP:</label>
                    <input type="text" value={otp} onChange={e => setOtp(e.target.value)} className="input-field-updated" />
                    <button type="button" onClick={verifyOtp}>Verify OTP</button>
                  </div>
                )}

                {otpVerified && (
                  <>
                    <div className="profile-row-updated">
                      <label>New Password:</label>
                      <input type="password" name="newPassword" value={formData.newPassword} onChange={handleChange} className="input-field-updated" />
                    </div>
                    <div className="profile-row-updated">
                      <label>Confirm Password:</label>
                      <input type="password" name="confirmPassword" value={formData.confirmPassword} onChange={handleChange} className="input-field-updated" />
                    </div>
                  </>
                )}
              </>
            )}

            <div className="profile-row-updated">
              <label>Push Notifications:</label>
              <input
                type="checkbox"
                checked={!!formData.push_notifications}
                onChange={() => setFormData(prev => ({ ...prev, push_notifications: prev.push_notifications ? 0 : 1 }))}
              />
            </div>
          </>
        )}

        <div className="profile-actions-updated">
          {editing ? (
            <>
              <button className="btn-save-updated" onClick={handleSave}>Save</button>
              <button className="btn-cancel-updated" onClick={handleEditToggle}>Cancel</button>
            </>
          ) : (
            <button className="btn-edit-updated" onClick={handleEditToggle}>Edit Profile</button>
          )}
        </div>

        {message && <p className="profile-message-updated">{message}</p>}
      </div>
    </div>
  );
}
