import React, { Component } from 'react';
import axios from "axios";
import "./Recipe.css";

class Recipe extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedItems: [],
      items: [
        "10 Bread loaves",
        "1L Milk Carton",
        "50g Mushrooms",
        "3 Eggs",
        "300g Rice",
        "5 Onions"
      ]
    };
  }

  toggleItem = (item) => {
    this.setState((prevState) => {
      const alreadySelected = prevState.selectedItems.includes(item);
      return {
        selectedItems: alreadySelected
          ? prevState.selectedItems.filter((i) => i !== item)
          : [...prevState.selectedItems, item]
      };
    });
  };

  handleGenerate = () => {
    // Later you can replace this with axios.post(...) to your backend/AI
    alert("Generating recipe with: " + this.state.selectedItems.join(", "));
  };

  render() {
    return (
      <div style={{ textAlign: "center", marginTop: "50px" }}>
        <h2>AI Recipe Generator</h2>
        <p>Select items to incorporate:</p>

        <div style={{ textAlign: "left", maxWidth: "300px", margin: "20px auto" }}>
          {this.state.items.map((item, index) => (
            <label key={index} style={{ display: "block", marginBottom: "10px" }}>
              <input
                type="checkbox"
                checked={this.state.selectedItems.includes(item)}
                onChange={() => this.toggleItem(item)}
              />
              {" "}{item}
            </label>
          ))}
        </div>

        <button
          onClick={this.handleGenerate}
          style={{
            backgroundColor: "green",
            color: "white",
            border: "none",
            padding: "10px 20px",
            borderRadius: "8px",
            cursor: "pointer",
            fontSize: "16px"
          }}
        >
          Generate
        </button>
      </div>
    );
  }
}

export default Recipe;
