/* Author: Bethlehem Shimelis
   Event: Sprint 1: Manually Input Food Items with Expiry dates
   LatestUpdate: Cartoon-style floating food background with dynamic donation logo
   Parameters: 
      goToRegister (function) - callback to switch to registration form
      onLogin (function) - callback after successful login
   Description: Handles user login with visually appealing floating food theme
*/
import { useState, useEffect, useMemo } from "react";
import axios from "axios";
import '../auth.css';

const API_BACKEND = process.env.REACT_APP_API_BACKEND;

const foodEmojis = [
  '🍎','🍌','🍊','🍐','🍉','🍇','🍓',
  '🥕','🌽','🥔','🥦','🥬','🧄','🧅',
  '🍞','🥖','🥯','🥐',
  '🥚','🧀','🥛',
  '🥫','🫙','🍲','🥣',
  '🍚','🍝','🌾',
  '🥜','🌰',
  '🍯','🍼','🧺',
  '🍪','🍫'
];

export default function LoginForm({ goToRegister, onLogin }) {
  const [form, setForm] = useState({ email: "", password: "" });
  const [otp, setOtp] = useState("");
  const [step, setStep] = useState("login"); // login | otp
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [emojiIndex, setEmojiIndex] = useState(0);
  const [resendCooldown, setResendCooldown] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setEmojiIndex(prev => (prev + 1) % foodEmojis.length);
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  // Cooldown timer for OTP resend
  useEffect(() => {
    if (resendCooldown <= 0) return;
    const timer = setInterval(() => {
      setResendCooldown(prev => prev - 1);
    }, 1000);
    return () => clearInterval(timer);
  }, [resendCooldown]);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });
  const handleOtpChange = (e) => setOtp(e.target.value);

  const requestOtp = async () => {
    try {
      await axios.post(`${API_BACKEND}/api/auth/request-otp`, { email: form.email });
      setSuccess("OTP sent! Check your email.");
      setStep("otp");
      setResendCooldown(30); // 30s cooldown
    } catch (err) {
      setError(err.response?.data?.error || "Failed to request OTP");
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(""); 
    setSuccess("");

    if (step === "login") {
      try {
        const resLogin = await axios.post(`${API_BACKEND}/api/auth/login`, form);
        setSuccess("Login successful!");
        if (onLogin) onLogin(resLogin.data.user);
        localStorage.setItem("loggedInUser", JSON.stringify(resLogin.data.user));
      } catch (loginErr) {
        const loginMsg = loginErr.response?.data?.error || "Login failed";

        // fallback to OTP
        if (loginMsg.includes("Invalid credentials") || loginMsg.includes("Email not registered")) {
          requestOtp();
        } else {
          setError(loginMsg);
        }
      }
    } else if (step === "otp") {
      try {
        const resVerify = await axios.post(`${API_BACKEND}/api/auth/verify-otp`, { email: form.email, otp });
        setSuccess(resVerify.data.message || "OTP verified!");
        if (onLogin) onLogin(resVerify.data.user);
        localStorage.setItem("loggedInUser", JSON.stringify(resVerify.data.user));
      } catch (err) {
        setError(err.response?.data?.error || "OTP verification failed");
      }
    }
  };

  const handleResend = () => {
    if (resendCooldown <= 0) requestOtp();
  };

  const emojiSpans = useMemo(() => {
    return Array.from({ length: 100 }, (_, i) => {
      const emoji = foodEmojis[i % foodEmojis.length];
      const left = Math.random() * 100;
      const duration = 10 + Math.random() * 6;
      const delay = Math.random() * 10;
      return (
        <span
          key={i}
          className="food-item"
          style={{
            left: `${left}%`,
            fontSize: '60px',
            animationDuration: `${duration}s`,
            animationDelay: `${delay}s`,
            opacity: 0,
            animationFillMode: 'forwards'
          }}
        >
          {emoji}
        </span>
      );
    });
  }, []);

  return (
    <div className="login-page">
      <div className="food-background">{emojiSpans}</div>
      <div className="login-card-wrapper">
        <form onSubmit={handleSubmit} className="form-card">
          <div className="form-header">
            <div className="logo-icon" style={{ fontSize: '3rem' }}>
              {foodEmojis[emojiIndex]}
            </div>
            <h1 className="app-title">{step === "login" ? "FoodSave Hub" : "Enter OTP"}</h1>
            <p className="subtitle">{step === "login" ? <em>Recreate, Donate, and Reduce Food Waste</em> : "Check your email for the OTP"}</p>
          </div>

          <input
            type="email"
            name="email"
            placeholder="Enter your email"
            value={form.email}
            onChange={handleChange}
            required
            className="input-field"
            disabled={step === "otp"}
          />

          {step === "login" && (
            <input
              type="password"
              name="password"
              placeholder="Enter your password"
              value={form.password}
              onChange={handleChange}
              required
              className="input-field"
            />
          )}

          {step === "otp" && (
            <>
              <input
                type="text"
                name="otp"
                placeholder="Enter OTP"
                value={otp}
                onChange={handleOtpChange}
                required
                className="input-field"
              />
              <button
                type="button"
                className="toggle-link"
                onClick={handleResend}
                disabled={resendCooldown > 0}
              >
                {resendCooldown > 0 ? `Resend OTP in ${resendCooldown}s` : "Resend OTP"}
              </button>
            </>
          )}

          {error && <p className="error-msg">{error}</p>}
          {success && <p className="success-msg">{success}</p>}

          <button type="submit" className="primary-btn">
            {step === "login" ? "Login" : "Verify OTP"}
          </button>

          {step === "login" && (
            <button type="button" className="toggle-link" onClick={goToRegister}>
              Don't have an account? <span>Sign Up</span>
            </button>
          )}
        </form>
      </div>
    </div>
  );
}
