import { useEffect, useState } from "react";
import axios from "axios";
import "./Notifications.css";


const API_BACKEND = process.env.REACT_APP_API_BACKEND;

export default function Notification({ currentUser, onRead, refreshFlag  }) {
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const unreadCount = notifications.filter(n => !n.notificationRead).length;

  
  // Fetch notifications from backend
  useEffect(() => {
    const fetchNotifications = async () => {
      try {
        const loggedInUser = JSON.parse(localStorage.getItem("loggedInUser"));

        const res = await axios.get(
          `${API_BACKEND}/api/notifications`,
          { params: { email: loggedInUser.email } }
        );
        setNotifications(res.data);
        setError("");
      } catch (err) {
        console.error("Fetch notifications error:", err);
        setError("Failed to load notifications.");
      } finally {
        setLoading(false);
      }
    };

    fetchNotifications();
  }, [refreshFlag]);

const handleDelete = async (id) => {
  const loggedInUser = JSON.parse(localStorage.getItem("loggedInUser"));

  try {
    await axios.put(`${API_BACKEND}/api/notifications/${id}/delete`, {}, {
      params: { email: loggedInUser.email }
    });

    // Update local state so the notification disappears
    setNotifications((prev) => prev.filter((item) => item.id !== id));
  } catch (err) {
    console.error("Failed to mark notification as deleted:", err);
  }
};



  // ✅ Mark a notification as read
  const handleMarkAsRead = async (id) => {
    const loggedInUser = JSON.parse(localStorage.getItem("loggedInUser"));
    
    try {
      await axios.put(
  `${API_BACKEND}/api/notifications/${id}/read`,
  {}, // empty body
  { params: { email: loggedInUser.email } } // this is correct usage
);

      // Update local state so the red dot disappears immediately
      setNotifications((prev) =>
        prev.map((item) =>
          item.id === id ? { ...item, notificationRead: true } : item
        )
      );

      // notify App to update unread count
      if (onRead) onRead(id);

    } catch (err) {
      console.error("Failed to mark notification as read:", err);
    }
  };

  if (loading) return <p>Loading notifications...</p>;
  if (error) return <p className="error-msg">{error}</p>;

  return (
    <div className="notification-container">
      <div className="notification-card">
        <h2 className="notification-title">Notifications</h2>

        {notifications.length === 0 ? (
          <p>No notifications available.</p>
        ) : (
          notifications.map((item) => (
            <div
              key={item.id}
              className="notification-item"
              onClick={() => handleMarkAsRead(item.id)}
            >
              <div className="notification-info">
                <p className="notification-title-text">
                  {item.name}
                  {!item.notificationRead && <span className="red-dot"></span>}
                </p>
                <p className="notification-meta">{item.expiryStatus}</p>
              </div>
              <div className="notification-actions">
                <span
                  className={`notification-status ${
                    item.status === "expired"
                      ? "status-expired"
                      : item.status === "warning"
                      ? "status-warning"
                      : "status-good"
                  }`}
                >
                  {item.status}
                </span>
                <button
                  className="action-btn"
                  onClick={(e) => {
                    e.stopPropagation();
                    handleDelete(item.id);
                  }}
                  title="Delete notification"
                >
                  ✕
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
