import { useEffect, useState } from "react";
import axios from "axios";

export default function HomeDash({ currentUser, onAddNew, refreshFlag }) {
  const [foodItems, setFoodItems] = useState([]);

  const fetchFoodItems = async () => {
    try {
      // GET request with email in query params
      const res = await axios.get(
        "https://foodsave-backend.onrender.com/api/users/fooditems",
        { params: { email: currentUser.email } }
      );
      setFoodItems(res.data.foodItems);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchFoodItems();
  }, [refreshFlag]);

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this item?")) return;

    try {
      // DELETE request now sends email in request body for authentication
      await axios.delete(
        `https://foodsave-backend.onrender.com/api/users/fooditems/${id}`,
        { data: { email: currentUser.email } }
      );
      fetchFoodItems();
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="table-form-container">
      <h1 className="table-title">Your Food Inventory</h1>

      <table className="form-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Expiry Date</th>
            <th>Quantity</th>
            <th>Category</th>
            <th>Measure per Unit</th>
            <th>Unit</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {foodItems.length === 0 ? (
            <tr>
              <td colSpan="7" className="no-items-msg">
                No items yet
              </td>
            </tr>
          ) : (
            foodItems.map((item) => (
              <tr key={item.fooditemid}>
                <td>{item.name}</td>
                <td>{new Date(item.expirydate).toLocaleDateString()}</td>
                <td>{item.quantity}</td>
                <td>{item.foodcategory}</td>
                <td>{item.measureperunit}</td>
                <td>{item.unit}</td>
                <td>
                  <button
                    className="delete-btn"
                    onClick={() => handleDelete(item.fooditemid)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>

      <div className="form-buttons">
        <button type="button" onClick={onAddNew}>
          Add New Food Item
        </button>
      </div>
    </div>
  );
}
