import React, { useEffect, useState } from "react";
import axios from "axios";

export default function FoodDash({ currentUser, onAddNew }) {
  const [foodItems, setFoodItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [editingId, setEditingId] = useState(null);
  const [editForm, setEditForm] = useState({});

  // Fetch all food items for the current user
  const fetchFoodItems = async () => {
    try {
      const res = await axios.get(
        `https://foodsave-backend.onrender.com/api/users/fooditems?email=${currentUser.email}`
      );
      setFoodItems(res.data.foodItems);
      setLoading(false);
    } catch (err) {
      console.error(err);
      setError("Failed to load items");
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchFoodItems();
  }, []);

  // Delete food item
  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this item?")) return;
    try {
      await axios.delete(
        `https://foodsave-backend.onrender.com/api/users/fooditems/${id}`,
        { data: { email: currentUser.email } }
      );
      setFoodItems(foodItems.filter((item) => item.fooditemid !== id));
    } catch (err) {
      console.error(err);
      setError("Failed to delete item");
    }
  };

  // Start editing a row
  const startEdit = (item) => {
    setEditingId(item.fooditemid);
    setEditForm({ ...item });
  };

  // Cancel editing
  const cancelEdit = () => {
    setEditingId(null);
    setEditForm({});
  };

  // Handle inline form change
  const handleChange = (e) => {
    setEditForm({ ...editForm, [e.target.name]: e.target.value });
  };

  // Save changes (PUT request)
  const handleSave = async () => {
    try {
      const { fooditemid, name, expirydate, quantity, foodcategory, donationid, Measure_per_Unit, Unit } = editForm;

      await axios.put(
        `https://foodsave-backend.onrender.com/api/users/fooditems/${fooditemid}`,
        {
          email: currentUser.email,
          name,
          expirydate,
          quantity,
          foodcategory,
          donationid,
          Measure_per_Unit,
          Unit,
        }
      );
      setEditingId(null);
      setEditForm({});
      fetchFoodItems(); // refresh table
    } catch (err) {
      console.error(err);
      setError("Failed to save changes");
    }
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p className="error-msg">{error}</p>;

  return (
    <div className="dashboard-container">
      <h2>My Food Items</h2>
      <table className="food-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Expiry</th>
            <th>Qty</th>
            <th>Category</th>
            <th>Measure</th>
            <th>Unit</th>
            <th>Donation ID</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {foodItems.map((item) => (
            <tr key={item.fooditemid}>
              <td>
                {editingId === item.fooditemid ? (
                  <input type="text" name="name" value={editForm.name} onChange={handleChange} />
                ) : (
                  item.name
                )}
              </td>
              <td>
                {editingId === item.fooditemid ? (
                  <input type="date" name="expirydate" value={editForm.expirydate.split("T")[0]} onChange={handleChange} />
                ) : (
                  new Date(item.expirydate).toLocaleDateString()
                )}
              </td>
              <td>
                {editingId === item.fooditemid ? (
                  <input type="number" name="quantity" value={editForm.quantity} onChange={handleChange} />
                ) : (
                  item.quantity
                )}
              </td>
              <td>
                {editingId === item.fooditemid ? (
                  <input type="text" name="foodcategory" value={editForm.foodcategory} onChange={handleChange} />
                ) : (
                  item.foodcategory
                )}
              </td>
              <td>
                {editingId === item.fooditemid ? (
                  <input type="number" name="Measure_per_Unit" value={editForm.Measure_per_Unit} onChange={handleChange} />
                ) : (
                  item.Measure_per_Unit
                )}
              </td>
              <td>
                {editingId === item.fooditemid ? (
                  <input type="text" name="Unit" value={editForm.Unit} onChange={handleChange} />
                ) : (
                  item.Unit
                )}
              </td>
              <td>
                {editingId === item.fooditemid ? (
                  <input type="text" name="donationid" value={editForm.donationid || ""} onChange={handleChange} />
                ) : (
                  item.donationid || "-"
                )}
              </td>
              <td>
                {editingId === item.fooditemid ? (
                  <>
                    <button className="edit-btn" onClick={handleSave}>Save</button>
                    <button className="cancel-btn" onClick={cancelEdit}>Cancel</button>
                  </>
                ) : (
                  <>
                    <button className="edit-btn" onClick={() => startEdit(item)}>Edit</button>
                    <button className="delete-btn" onClick={() => handleDelete(item.fooditemid)}>Delete</button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <button className="add-btn" onClick={onAddNew}>
        Add New Food Item
      </button>
    </div>
  );
}
