// src/users/HomeUser.js
import React, { useState } from "react";
import HomeDash from "./HomeComponents/HomeDash";
import HomeForm from "./HomeComponents/HomeForm";
import "./HomeUser.css";

export default function HomeUser({ currentUser }) {
  if (!currentUser || !currentUser.stakeholderID) {
    return (
      <div className="dashboard-container">
        <h2>Error: User not logged in.</h2>
        <p>Please log in again to access your dashboard.</p>
      </div>
    );
  }

  const [showForm, setShowForm] = useState(false);
  const [refreshFlag, setRefreshFlag] = useState(false);

  const handleAddNew = () => setShowForm(true);

  const handleCloseForm = () => {
    setShowForm(false);
    setRefreshFlag((prev) => !prev);
  };

  return (
    <div className="dashboard-container">
      {showForm ? (
        <HomeForm
          currentUser={currentUser} // ensure stakeholderID is included
          onClose={handleCloseForm}
        />
      ) : (
        <HomeDash
          currentUser={currentUser} // ensure stakeholderID is included
          onAddNew={handleAddNew}
          refreshFlag={refreshFlag}
        />
      )}
    </div>
  );
}
