//determing pending donations count
const API_BACKEND = 'http://localhost:5000 ';
//process.env.REACT_APP_API_BACKEND;
export async function getPendingDonationCount() {
  try {
    const res = await fetch(`${API_BACKEND}/pending-donations/count`);
    const data = await res.json();
    return data.count || 0;
  } catch (err) {
    console.error('Failed to fetch pending donation count:', err);
    return 0;
  }
}
