import { useEffect, useState } from "react";
import axios from "axios";
import "../HomeUser.css";

export default function HomeDash({ currentUser, onAddNew }) {
  const [foodItems, setFoodItems] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [editedItem, setEditedItem] = useState({});
  const [rowError, setRowError] = useState({});
  const [deleteConfirmId, setDeleteConfirmId] = useState(null);

  const fetchFoodItems = async () => {
    try {
      const res = await axios.get(
        `https://foodsave-backend.onrender.com/api/users/fooditems?email=${currentUser.email}`
      );
      setFoodItems(res.data.foodItems);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchFoodItems();
  }, []);

  const getExpiryClass = (expirydate) => {
    const today = new Date();
    const expiry = new Date(expirydate);
    const diffDays = Math.ceil((expiry - today) / (1000 * 60 * 60 * 24));
    if (diffDays < 0) return "expired";
    if (diffDays <= 2) return "expiring-soon";
    return "fresh";
  };

  const handleEditClick = (item) => {
    setEditingId(item.fooditemid);
    setEditedItem({ ...item });
    setRowError({});
  };

  const handleCancelEdit = () => {
    setEditingId(null);
    setEditedItem({});
    setRowError({});
  };

  const handleInputChange = (e) => {
    setEditedItem({ ...editedItem, [e.target.name]: e.target.value });
  };

  const handleSave = async (id) => {
    const today = new Date();
    today.setHours(0,0,0,0);
    const expiry = new Date(editedItem.expirydate);

    // Inline validation
    const error = {};
    if (!editedItem.name || editedItem.name.trim() === "") error.name = "Name required";
    if (!editedItem.expirydate || expiry <= today) error.expirydate = "Expiry must be future";

    if (Object.keys(error).length > 0) {
      setRowError(error);
      return;
    }

    try {
      await axios.put(
        `https://foodsave-backend.onrender.com/api/users/fooditems/${id}`,
        { ...editedItem, email: currentUser.email }
      );
      setEditingId(null);
      setEditedItem({});
      setRowError({});
      fetchFoodItems();
    } catch (err) {
      console.error("Failed to save edit:", err.response?.data || err);
    }
  };

  const handleDeleteRequest = (id) => {
    setDeleteConfirmId(id);
  };

  const handleConfirmDelete = async (id) => {
    try {
      await axios.delete(
        `https://foodsave-backend.onrender.com/api/users/fooditems/${id}?email=${currentUser.email}`
      );
      setDeleteConfirmId(null);
      fetchFoodItems();
    } catch (err) {
      console.error("Failed to delete item:", err.response?.data || err);
    }
  };

  const handleCancelDelete = () => {
    setDeleteConfirmId(null);
  };

  return (
    <div className="table-container">
      <h1 className="table-title">Inventory</h1>

      <div className="filter-container">
        <label>Filter by category: </label>
        <select
          onChange={(e) => setFoodItems(
            e.target.value
              ? foodItems.filter(f => f.foodcategory === e.target.value)
              : foodItems
          )}
        >
          <option value="">All</option>
          {[
            "Produce","Dairy & Eggs","Meat","Seafood","Bakery","Grains",
            "Frozen Foods","Beverages","Snacks","Prepared Meals","Other",
          ].map(cat => <option key={cat} value={cat}>{cat}</option>)}
        </select>
      </div>

      <table className="food-table">
        <thead>
          <tr>
            <th>Category</th>
            <th>Name</th>
            <th>Expiry Date</th>
            <th>Quantity</th>
            <th>Measure</th>
            <th>Unit</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {foodItems
            .sort((a,b) => new Date(a.expirydate) - new Date(b.expirydate))
            .map((item) => (
              <tr key={item.fooditemid} className={getExpiryClass(item.expirydate)}>
                <td>{editingId===item.fooditemid
                  ? <select name="foodcategory" value={editedItem.foodcategory} onChange={handleInputChange}>
                      {[
                        "Produce","Dairy & Eggs","Meat","Seafood","Bakery","Grains",
                        "Frozen Foods","Beverages","Snacks","Prepared Meals","Other",
                      ].map(cat => <option key={cat} value={cat}>{cat}</option>)}
                    </select>
                  : item.foodcategory
                }</td>

                <td>
                  {editingId===item.fooditemid
                    ? <div>
                        <input name="name" value={editedItem.name} onChange={handleInputChange} />
                        {rowError.name && <div className="inline-error">{rowError.name}</div>}
                      </div>
                    : item.name
                  }
                </td>

                <td>
                  {editingId===item.fooditemid
                    ? <div>
                        <input type="date" name="expirydate" value={editedItem.expirydate.slice(0,10)} onChange={handleInputChange} />
                        {rowError.expirydate && <div className="inline-error">{rowError.expirydate}</div>}
                      </div>
                    : new Date(item.expirydate).toLocaleDateString()
                  }
                </td>

                <td>
                  {editingId===item.fooditemid
                    ? <input type="number" name="quantity" value={editedItem.quantity} onChange={handleInputChange} />
                    : item.quantity
                  }
                </td>

                <td>
                  {editingId===item.fooditemid
                    ? <input type="number" name="Measure_per_Unit" value={editedItem.Measure_per_Unit} onChange={handleInputChange} />
                    : item.Measure_per_Unit
                  }
                </td>

                <td>
                  {editingId===item.fooditemid
                    ? <select name="Unit" value={editedItem.Unit} onChange={handleInputChange}>
                        {["ml","L","g","kg"].map(u => <option key={u} value={u}>{u}</option>)}
                      </select>
                    : item.Unit
                  }
                </td>

                <td>
                  {editingId===item.fooditemid ? (
                    <>
                      <button className="btn btn-orange" onClick={() => handleSave(item.fooditemid)}>Save</button>
                      <button className="btn btn-gray" onClick={handleCancelEdit}>Cancel</button>
                    </>
                  ) : deleteConfirmId===item.fooditemid ? (
                    <div className="delete-inline">
                      <span>Are you sure?</span>
                      <button className="btn btn-red" onClick={() => handleConfirmDelete(item.fooditemid)}>Yes</button>
                      <button className="btn btn-gray" onClick={handleCancelDelete}>No</button>
                    </div>
                  ) : (
                    <>
                      <button className="btn btn-orange" onClick={() => handleEditClick(item)}>Edit</button>
                      <button className="btn btn-red" onClick={() => handleDeleteRequest(item.fooditemid)}>Delete</button>
                    </>
                  )}
                </td>
              </tr>
            ))
          }
        </tbody>
      </table>

      <div className="add-btn-container">
        <button className="btn btn-green" onClick={onAddNew}>Add Food Item</button>
      </div>
    </div>
  );
}
