import React, { Component } from 'react';
import './Notifications.css';

class Notification extends Component {
  constructor(props) {
    super(props);
    this.state = {
      notifications: [], // database notifications will be stored here
      loading: true,
      error: null,
    };
  }

  componentDidMount() {
    this.fetchNotifications();
  }

  // Fetch notifications from backend API
  fetchNotifications = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/notifications"); // your backend endpoint
      if (!response.ok) {
        throw new Error("Failed to fetch notifications");
      }
      const data = await response.json();
      this.setState({ notifications: data, loading: false });
    } catch (error) {
      this.setState({ error: error.message, loading: false });
    }
  };

  render() {
    const { notifications, loading, error } = this.state;

    if (loading) return <p>Loading notifications...</p>;
    if (error) return <p>Error: {error}</p>;

    return (
      <div className="notification-page">
        <h2 className="notification-title">Notifications</h2>

        {notifications.length === 0 ? (
          <p>No notifications available.</p>
        ) : (
          notifications.map((item, index) => (
            <div key={index} className="notification-card">
              <div className="notification-info">
                <p className="notification-title-text">{item.name}</p>
                <p className="notification-meta">
                  {item.expiryStatus} {/* e.g. "Expires in 2 days" */}
                </p>
              </div>
              <span
                className={`notification-status ${
                  item.status === "expired"
                    ? "status-expired"
                    : item.status === "warning"
                    ? "status-warning"
                    : "status-good"
                }`}
              >
                {item.status}
              </span>
            </div>
          ))
        )}
      </div>
    );
  }
}

export default Notification;
