/* Author: Bethlehem Shimelis
   Event: Sprint 1: Manually Input Food Items with Expiry dates
   LatestUpdate: Added conditional capacity field for charity users
   parameters: user input
   Description: Processes User requests for Login and Registration
   Returns: posts to the API backend
*/

import { useState } from "react";
import axios from "axios";

export default function RegisterForm({ goToLogin, onLogin }) {
  const [form, setForm] = useState({
    accountType: "Household/Individual",
    name: "",
    email: "",
    region: "Durban, Kwa-Zulu Natal", // ✅ renamed province -> region
    password: "",
    capacity: "", // new field for charities
  });

  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate capacity if charity
    if (form.accountType === "Charity/Foodbank" && (!form.capacity || isNaN(form.capacity) || Number(form.capacity) < 0)) {
      setError("Please enter a valid capacity for charity users");
      setSuccess("");
      return;
    }

    try {
      const payload = {
        accountType: form.accountType,
        name: form.name,
        email: form.email,
        password: form.password,
        region: form.region,
        capacity: form.accountType === "Charity/Foodbank" ? Number(form.capacity) : undefined,
      };

      const res = await axios.post(
        "https://foodsave-backend.onrender.com/api/auth/register",
        payload
      );
      setSuccess(res.data.message || "Registration successful!");
      setError("");

      if (onLogin) onLogin(res.data.user);
    } catch (err) {
      console.error("Register error:", err);
      setError(err.response?.data?.error || "Something went wrong. Try again.");
      setSuccess("");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="form-card">
      <h1>FOODSAVE HUB</h1>

      <select
        name="accountType"
        value={form.accountType}
        onChange={handleChange}
      >
        <option>Household/Individual</option>
        <option>Business/Corporate</option>
        <option>Charity/Foodbank</option>
      </select>

      <input
        type="text"
        name="name"
        placeholder="Name"
        value={form.name}
        onChange={handleChange}
        required
      />
      <input
        type="email"
        name="email"
        placeholder="Email"
        value={form.email}
        onChange={handleChange}
        required
      />
      <input
        type="password"
        name="password"
        placeholder="Password"
        value={form.password}
        onChange={handleChange}
        required
      />

      <select
        name="region"
        value={form.region}
        onChange={handleChange}
      >
        <option>Durban, Kwa-Zulu Natal</option>
        <option>Capetown, Western Cape</option>
        <option>Pretoria, Gauteng</option>
      </select>

      {/* Conditional capacity input for charities */}
      {form.accountType === "Charity/Foodbank" && (
        <input
          type="number"
          name="capacity"
          placeholder="Capacity"
          value={form.capacity}
          min="0"
          onChange={handleChange}
          required
        />
      )}

      {error && <p className="error-msg">{error}</p>}
      {success && <p className="success-msg">{success}</p>}

      <button type="submit">Create Account</button>
      <button
        type="button"
        className="toggle-link"
        onClick={goToLogin}
      >
        Already have an account? Login
      </button>
    </form>
  );
}
