import { useState } from "react";
import axios from "axios";

export default function HomeForm({ currentUser, onClose, onRefresh }) {
  const [form, setForm] = useState({
    name: "",
    expirydate: "",
    quantity: 1,
    foodcategory: "",
    donationid: "",
    Measure_per_Unit: 1,
    Unit: "g",
  });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const foodCategories = [
    "Produce",
    "Dairy & Eggs",
    "Meat",
    "Seafood",
    "Bakery",
    "Grains",
    "Frozen Foods",
    "Beverages",
    "Snacks",
    "Prepared Meals",
    "Other",
  ];

  const units = ["ml", "L", "g", "kg"];

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    // Basic validation
    if (!form.name) return setError("Name is required");
    if (!form.expirydate || new Date(form.expirydate) <= new Date())
      return setError("Expiry date must be in the future");
    if (form.quantity <= 0) return setError("Quantity must be at least 1");
    if (form.Measure_per_Unit <= 0)
      return setError("Measure per unit must be at least 1");
    if (!form.foodcategory) return setError("Please select a food category");

    try {
      const res = await axios.post(
        "https://foodsave-backend.onrender.com/api/users/fooditems",
        {
          ...form,
          stakeholderID: currentUser.stakeholderID, // send the correct stakeholderID
        }
      );

      setSuccess("Food item added!");
      setForm({
        name: "",
        expirydate: "",
        quantity: 1,
        foodcategory: "",
        donationid: "",
        Measure_per_Unit: 1,
        Unit: "g",
      });

      if (onRefresh) onRefresh(); // refresh dashboard
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.error || "Failed to add food item");
    }
  };

  return (
    <div className="table-form-container">
      <h1 className="table-title">Add Food Item</h1>

      <form onSubmit={handleSubmit}>
        <table className="form-table">
          <tbody>
            <tr>
              <td>Name:</td>
              <td>
                <input
                  type="text"
                  name="name"
                  value={form.name}
                  onChange={handleChange}
                  required
                />
              </td>
            </tr>
            <tr>
              <td>Expiry Date:</td>
              <td>
                <input
                  type="date"
                  name="expirydate"
                  value={form.expirydate}
                  onChange={handleChange}
                  required
                />
              </td>
            </tr>
            <tr>
              <td>Quantity:</td>
              <td>
                <input
                  type="number"
                  name="quantity"
                  min="1"
                  value={form.quantity}
                  onChange={handleChange}
                  required
                />
              </td>
            </tr>
            <tr>
              <td>Category:</td>
              <td>
                <select
                  name="foodcategory"
                  value={form.foodcategory}
                  onChange={handleChange}
                  required
                >
                  <option value="">Select Category</option>
                  {foodCategories.map((cat) => (
                    <option key={cat} value={cat}>
                      {cat}
                    </option>
                  ))}
                </select>
              </td>
            </tr>
            <tr>
              <td>Donation ID:</td>
              <td>
                <input
                  type="text"
                  name="donationid"
                  value={form.donationid}
                  onChange={handleChange}
                  placeholder="Optional"
                />
              </td>
            </tr>
            <tr>
              <td>Measure per Unit:</td>
              <td>
                <input
                  type="number"
                  name="Measure_per_Unit"
                  min="1"
                  value={form.Measure_per_Unit}
                  onChange={handleChange}
                  required
                />{" "}
                <select
                  name="Unit"
                  value={form.Unit}
                  onChange={handleChange}
                  required
                >
                  {units.map((u) => (
                    <option key={u} value={u}>
                      {u}
                    </option>
                  ))}
                </select>
              </td>
            </tr>
          </tbody>
        </table>

        {error && <p className="error-msg">{error}</p>}
        {success && <p className="success-msg">{success}</p>}

        <div className="form-buttons">
          <button type="submit">Add Item</button>
          <button type="button" onClick={onClose}>
            Back to Inventory
          </button>
        </div>
      </form>
    </div>
  );
}
