import React, { useEffect, useState } from "react";
import axios from "axios";

function getExpiryClass(expirydate) {
  const today = new Date();
  const expiry = new Date(expirydate);
  const diffTime = expiry - today;
  const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
  
  if (diffDays < 0) return "expired"; // past
  if (diffDays <= 2) return "expiring-soon"; // less than 2 days
  return "";
}


export default function HomeDash({ currentUser, onAddNew }) {
  const [foodItems, setFoodItems] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [editForm, setEditForm] = useState({});

  const fetchFoodItems = async () => {
    try {
      const res = await axios.get(
        `https://foodsave-backend.onrender.com/api/users/fooditems?email=${currentUser.email}`
      );
      const sorted = res.data.foodItems.sort(
        (a, b) => new Date(a.expirydate) - new Date(b.expirydate)
      );
      setFoodItems(sorted);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchFoodItems();
  }, []);

  const handleEditClick = (item) => {
    setEditingId(item.fooditemid);
    setEditForm(item);
  };

  const handleEditChange = (e) => {
    setEditForm({ ...editForm, [e.target.name]: e.target.value });
  };

  const handleSave = async (id) => {
    try {
      await axios.put(
        `https://foodsave-backend.onrender.com/api/users/fooditems/${id}`,
        editForm
      );
      setEditingId(null);
      fetchFoodItems();
    } catch (err) {
      console.error(err);
      alert("Failed to update item");
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this item?")) return;
    try {
      await axios.delete(
        `https://foodsave-backend.onrender.com/api/users/fooditems/${id}?email=${currentUser.email}`
      );
      fetchFoodItems();
    } catch (err) {
      console.error(err);
      alert("Failed to delete item");
    }
  };

  return (
    <div className="table-form-container">
      <h1 className="table-title">Food Inventory</h1>
      <table className="dashboard-table">
        <thead>
          <tr>
            <th className="name-col">Name</th>
            <th className="expiry-col">Expiry Date</th>
            <th className="qty-col">Quantity</th>
            <th className="category-col">Category</th>
            <th className="measure-col">Measure per Unit</th>
            <th className="unit-col">Unit</th>
            <th className="action-col">Action</th>
          </tr>
        </thead>
        <tbody>
          {foodItems.map((item) => (
            <tr key={item.fooditemid}>
              <td className="name-col">
                {editingId === item.fooditemid ? (
                  <input
                    name="name"
                    value={editForm.name}
                    onChange={handleEditChange}
                  />
                ) : (
                  item.name
                )}
              </td>
              <td className="expiry-col">
                {editingId === item.fooditemid ? (
                  <input
                    type="date"
                    name="expirydate"
                    value={editForm.expirydate.slice(0, 10)}
                    onChange={handleEditChange}
                  />
                ) : (
                  new Date(item.expirydate).toLocaleDateString()
                )}
              </td>
              <td className="qty-col">
                {editingId === item.fooditemid ? (
                  <input
                    type="number"
                    name="quantity"
                    value={editForm.quantity}
                    onChange={handleEditChange}
                  />
                ) : (
                  item.quantity
                )}
              </td>
              <td className="category-col">
                {editingId === item.fooditemid ? (
                  <input
                    name="foodcategory"
                    value={editForm.foodcategory}
                    onChange={handleEditChange}
                  />
                ) : (
                  item.foodcategory
                )}
              </td>
              <td className="measure-col">
                {editingId === item.fooditemid ? (
                  <input
                    type="number"
                    name="Measure_per_Unit"
                    value={editForm.Measure_per_Unit}
                    onChange={handleEditChange}
                  />
                ) : (
                  item.Measure_per_Unit
                )}
              </td>
              <td className="unit-col">
                {editingId === item.fooditemid ? (
                  <input
                    name="Unit"
                    value={editForm.Unit}
                    onChange={handleEditChange}
                  />
                ) : (
                  item.Unit
                )}
              </td>
              <td className="action-col">
                {editingId === item.fooditemid ? (
                  <>
                    <button
                      className="btn btn-orange"
                      onClick={() => handleSave(item.fooditemid)}
                    >
                      Save
                    </button>
                    <button
                      className="btn btn-gray"
                      onClick={() => setEditingId(null)}
                    >
                      Cancel
                    </button>
                  </>
                ) : (
                  <>
                    <button
                      className="btn btn-orange"
                      onClick={() => handleEditClick(item)}
                    >
                      Edit
                    </button>
                    <button
                      className="btn btn-red"
                      onClick={() => handleDelete(item.fooditemid)}
                    >
                      Delete
                    </button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <div style={{ textAlign: "center", marginTop: "20px" }}>
        <button className="btn btn-green" onClick={onAddNew}>
          Add New Food Item
        </button>
      </div>
    </div>
  );
}
