import React, { useState } from "react";
import axios from "axios";

export default function HomeForm({ currentUser, onClose }) {
  const [form, setForm] = useState({
    name: "",
    expiryDate: "",
    quantity: 1,
    foodCategory: "",
    measurePerUnit: 1,
    unit: "g",
  });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const foodCategories = [
    "Produce",
    "Dairy & Eggs",
    "Meat",
    "Seafood",
    "Bakery",
    "Grains",
    "Frozen Foods",
    "Beverages",
    "Snacks",
    "Prepared Meals",
    "Other",
  ];

  const units = ["ml", "L", "g", "kg"];

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    if (!form.name) return setError("Name is required");
    if (!form.expiryDate || new Date(form.expiryDate) <= new Date())
      return setError("Expiry date must be in the future");
    if (form.quantity <= 0) return setError("Quantity must be at least 1");
    if (form.measurePerUnit <= 0)
      return setError("Measure per unit must be at least 1");
    if (!form.foodCategory) return setError("Please select a food category");

    try {
      await axios.post(
        "https://foodsave-backend.onrender.com/api/users/fooditems",
        {
          name: form.name,
          expirydate: form.expiryDate,
          quantity: form.quantity,
          foodcategory: form.foodCategory,
          measure_per_unit: form.measurePerUnit,
          unit: form.unit,
          email: currentUser.email, // important for backend auth
        }
      );

      setSuccess("Food item added!");
      setForm({
        name: "",
        expiryDate: "",
        quantity: 1,
        foodCategory: "",
        measurePerUnit: 1,
        unit: "g",
      });

      if (onClose) onClose(); // closes form and triggers refresh
    } catch (err) {
      setError(err.response?.data?.error || "Failed to add item");
    }
  };

  return (
    <div className="table-form-container">
      <h1 className="table-title">Add Food Item</h1>

      <form onSubmit={handleSubmit}>
        <table className="form-table">
          <tbody>
            <tr>
              <td>Name:</td>
              <td>
                <input
                  type="text"
                  name="name"
                  value={form.name}
                  onChange={handleChange}
                  required
                />
              </td>
            </tr>
            <tr>
              <td>Expiry Date:</td>
              <td>
                <input
                  type="date"
                  name="expiryDate"
                  value={form.expiryDate}
                  onChange={handleChange}
                  required
                />
              </td>
            </tr>
            <tr>
              <td>Quantity:</td>
              <td>
                <input
                  type="number"
                  name="quantity"
                  min="1"
                  value={form.quantity}
                  onChange={handleChange}
                  required
                />
              </td>
            </tr>
            <tr>
              <td>Category:</td>
              <td>
                <select
                  name="foodCategory"
                  value={form.foodCategory}
                  onChange={handleChange}
                  required
                >
                  <option value="">Select Category</option>
                  {foodCategories.map((cat) => (
                    <option key={cat} value={cat}>
                      {cat}
                    </option>
                  ))}
                </select>
              </td>
            </tr>
            <tr>
              <td>Measure per unit:</td>
              <td>
                <input
                  type="number"
                  name="measurePerUnit"
                  min="1"
                  value={form.measurePerUnit}
                  onChange={handleChange}
                  required
                />{" "}
                <select
                  name="unit"
                  value={form.unit}
                  onChange={handleChange}
                  required
                >
                  {units.map((u) => (
                    <option key={u} value={u}>
                      {u}
                    </option>
                  ))}
                </select>
              </td>
            </tr>
          </tbody>
        </table>

        {error && <p className="error-msg">{error}</p>}
        {success && <p className="success-msg">{success}</p>}

        <div className="form-buttons">
          <button type="submit">Add Item</button>
          <button type="button" onClick={onClose}>
            Back to Inventory
          </button>
        </div>
      </form>
    </div>
  );
}
