/* Author: Bethlehem Shimelis
   Event: FoodSave Hub 2025
   Description: Home dashboard for household users - view, edit, delete food items with inline delete confirmation */

import { useEffect, useState } from "react";
import axios from "axios";
import "../HomeUser.css";

export default function HomeDash({ currentUser, onAddNew }) {
  const [foodItems, setFoodItems] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [editedItem, setEditedItem] = useState({});
  const [deleteConfirmId, setDeleteConfirmId] = useState(null);
  const [categoryFilter, setCategoryFilter] = useState("All");

  const fetchFoodItems = async () => {
    try {
      const res = await axios.get(
        `https://foodsave-backend.onrender.com/api/users/fooditems?email=${currentUser.email}`
      );
      setFoodItems(res.data.foodItems);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchFoodItems();
  }, []);

  const getExpiryClass = (expirydate) => {
    const today = new Date();
    const expiry = new Date(expirydate);
    const diffDays = Math.ceil((expiry - today) / (1000 * 60 * 60 * 24));
    if (diffDays < 0) return "expired";
    if (diffDays <= 2) return "expiring-soon";
    if (diffDays <= 7) return "expiring-week";
    return "fresh";
  };

  const handleEditClick = (item) => {
    setEditingId(item.fooditemid);
    setEditedItem({ ...item });
  };

  const handleCancelEdit = () => {
    setEditingId(null);
    setEditedItem({});
  };

  const handleInputChange = (e) => {
    setEditedItem({ ...editedItem, [e.target.name]: e.target.value });
  };

  const handleSave = async (id) => {
    // Validation
    if (!editedItem.name || editedItem.name.trim() === "") {
      alert("Name cannot be blank.");
      return;
    }
    const expiry = new Date(editedItem.expirydate);
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    if (!editedItem.expirydate || expiry <= today) {
      alert("Expiry date must be in the future.");
      return;
    }

    try {
      await axios.put(
        `https://foodsave-backend.onrender.com/api/users/fooditems/${id}`,
        { ...editedItem, email: currentUser.email }
      );
      setEditingId(null);
      setEditedItem({});
      fetchFoodItems();
    } catch (err) {
      console.error("Failed to save edit:", err.response?.data || err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(
        `https://foodsave-backend.onrender.com/api/users/fooditems/${id}?email=${currentUser.email}`
      );
      setDeleteConfirmId(null);
      fetchFoodItems();
    } catch (err) {
      console.error("Failed to delete item:", err.response?.data || err);
    }
  };

  const categories = [
    "All",
    "Produce",
    "Dairy & Eggs",
    "Meat",
    "Seafood",
    "Bakery",
    "Grains",
    "Frozen Foods",
    "Beverages",
    "Snacks",
    "Prepared Meals",
    "Other",
  ];

  return (
    <div className="table-container">
      <h1 className="table-title">Inventory</h1>

      <div className="category-filter">
        <label>Filter by category: </label>
        <select
          value={categoryFilter}
          onChange={(e) => setCategoryFilter(e.target.value)}
        >
          {categories.map((cat) => (
            <option key={cat} value={cat}>{cat}</option>
          ))}
        </select>
      </div>

      <table className="food-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Expiry Date</th>
            <th>Quantity</th>
            <th>Category</th>
            <th>Measure</th>
            <th>Unit</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {foodItems
            .filter((item) => categoryFilter === "All" || item.foodcategory === categoryFilter)
            .sort((a, b) => new Date(a.expirydate) - new Date(b.expirydate))
            .map((item) => (
              <tr key={item.fooditemid} className={getExpiryClass(item.expirydate)}>
                <td>
                  {editingId === item.fooditemid ? (
                    <input
                      name="name"
                      value={editedItem.name}
                      onChange={handleInputChange}
                    />
                  ) : (
                    item.name
                  )}
                </td>
                <td>
                  {editingId === item.fooditemid ? (
                    <input
                      type="date"
                      name="expirydate"
                      value={editedItem.expirydate.slice(0, 10)}
                      onChange={handleInputChange}
                    />
                  ) : (
                    new Date(item.expirydate).toLocaleDateString()
                  )}
                </td>
                <td>
                  {editingId === item.fooditemid ? (
                    <input
                      type="number"
                      name="quantity"
                      value={editedItem.quantity}
                      onChange={handleInputChange}
                    />
                  ) : (
                    item.quantity
                  )}
                </td>
                <td>
                  {editingId === item.fooditemid ? (
                    <select
                      name="foodcategory"
                      value={editedItem.foodcategory}
                      onChange={handleInputChange}
                    >
                      {categories.slice(1).map((cat) => (
                        <option key={cat} value={cat}>{cat}</option>
                      ))}
                    </select>
                  ) : (
                    item.foodcategory
                  )}
                </td>
                <td>
                  {editingId === item.fooditemid ? (
                    <input
                      type="number"
                      name="Measure_per_Unit"
                      value={editedItem.Measure_per_Unit}
                      onChange={handleInputChange}
                    />
                  ) : (
                    item.Measure_per_Unit
                  )}
                </td>
                <td>
                  {editingId === item.fooditemid ? (
                    <select
                      name="Unit"
                      value={editedItem.Unit}
                      onChange={handleInputChange}
                    >
                      {["ml", "L", "g", "kg"].map((u) => (
                        <option key={u} value={u}>{u}</option>
                      ))}
                    </select>
                  ) : (
                    item.Unit
                  )}
                </td>
                <td>
                  {editingId === item.fooditemid ? (
                    <>
                      <button
                        className="btn btn-orange"
                        onClick={() => handleSave(item.fooditemid)}
                      >
                        Save
                      </button>
                      <button
                        className="btn btn-gray"
                        onClick={handleCancelEdit}
                      >
                        Cancel
                      </button>
                    </>
                  ) : (
                    <>
                      <button
                        className="btn btn-orange"
                        onClick={() => handleEditClick(item)}
                      >
                        Edit
                      </button>

                      {deleteConfirmId === item.fooditemid ? (
                        <span className="delete-confirm-text">
                          Are you sure?
                          <button
                            className="btn btn-red btn-small"
                            onClick={() => handleDelete(item.fooditemid)}
                          >
                            Yes
                          </button>
                          <button
                            className="btn btn-gray btn-small"
                            onClick={() => setDeleteConfirmId(null)}
                          >
                            No
                          </button>
                        </span>
                      ) : (
                        <button
                          className="btn btn-red"
                          onClick={() => setDeleteConfirmId(item.fooditemid)}
                        >
                          Delete
                        </button>
                      )}
                    </>
                  )}
                </td>
              </tr>
            ))}
        </tbody>
      </table>

      <div className="add-btn-container">
        <button className="btn btn-green" onClick={onAddNew}>
          Add Food Item
        </button>
      </div>
    </div>
  );
}
