// src/users/HomeUser.js
import React, { useState } from "react";
import HomeDash from ".HomeComponents/HomeDash";
import HomeForm from "./HomeForm";

export default function HomeUser({ currentUser }) {
  const [showForm, setShowForm] = useState(false);

  return (
    <div style={{ padding: "20px" }}>
      <h2 style={{ textAlign: "center" }}>Home User Dashboard</h2>

      {showForm ? (
        <HomeForm currentUser={currentUser} onClose={() => setShowForm(false)} />
      ) : (
        <>
          <button
            style={{ marginBottom: "20px", backgroundColor: "#16a34a", color: "#fff", padding: "10px", borderRadius: "8px", border: "none", cursor: "pointer" }}
            onClick={() => setShowForm(true)}
          >
            Add Food Item
          </button>
          <HomeDash currentUser={currentUser} />
        </>
      )}
    </div>
  );
}
