import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import supabase from "../supabaseClient";
import "./charity.css";

const Donor = ({ currentUser, userType }) => {
  const currentUserId = currentUser.stakeholderID;
  const [charities, setCharities] = useState([]);
  const [loading, setLoading] = useState(true);
  const [donationUpdates, setDonationUpdates] = useState([]);
  const navigate = useNavigate();

  // ✅ Fetch charities in same region
  useEffect(() => {
    const fetchCharities = async () => {
      if (!currentUserId) {
        console.warn("currentUserId is missing!");
        setLoading(false);
        return;
      }

      try {
        const { data: userData, error: userError } = await supabase
          .from("stakeholderdb")
          .select("region")
          .eq("stakeholderid", currentUserId)
          .maybeSingle();

        if (userError) throw userError;
        if (!userData) {
          console.warn("No user found with ID:", currentUserId);
          setLoading(false);
          return;
        }

        const { data: charityData, error: charityError } = await supabase
          .from("stakeholderdb")
          .select("*")
          .like("stakeholderid", "c%")
          .eq("region", userData.region);

        if (charityError) throw charityError;
        setCharities(charityData || []);
      } catch (err) {
        console.error("Unexpected error fetching charities:", err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchCharities();
  }, [currentUserId]);

  // ✅ Subscribe to donation updates
  useEffect(() => {
    if (!currentUserId) return;

    console.log("Subscribing for donor updates with ID:", currentUserId);

    const subscription = supabase
      .channel("donation-updates")
      .on(
        "postgres_changes",
        {
          event: "*",
          schema: "public",
          table: "donationdb",
          filter: `donorid=eq.${currentUserId}`, // ✅ must match DB
        },
        async (payload) => {
          console.log("Donation event received:", payload);

          const { donationstatus, donationid, charityid, donation_items } =
            payload.new || {};

          const { data: charity } = await supabase
            .from("stakeholderdb")
            .select("name")
            .eq("stakeholderid", charityid)
            .maybeSingle();

          let message = "";
          if (donationstatus === "approved") {
            const items = (donation_items || [])
              .map((i) => i.name)
              .join(", ");
            message = `${charity?.name || charityid} has accepted your donation${
              items ? ` (${items})` : ""
            }.`;
          } else if (donationstatus === "unapproved") {
            message = `${charity?.name || charityid} has declined your donation.`;
          } else {
            message = `${charity?.name || charityid} updated donation ${donationid} (${donationstatus}).`;
          }

          setDonationUpdates((prev) => [
            ...prev.filter((d) => d.donationid !== donationid),
            { donationid, message },
          ]);
        }
      )
      .subscribe((status) => {
        console.log("Subscription status:", status);
      });

    return () => {
      supabase.removeChannel(subscription);
    };
  }, [currentUserId]);

  const handleDonate = (charity) => {
    navigate("/grocerylist", {
      state: { foodbank: charity, userId: currentUserId },
    });
  };

  if (loading) return <p>Loading charities...</p>;

  return (
    <div>
      <h2 id="donations-title">Donations</h2>
      <p>
        Donate to a Food bank <strong>{userType}</strong>
      </p>

      {/* ✅ Donation status messages */}
      <div className="donation-status">
        {donationUpdates.map((update) => (
          <p key={update.donationid}>{update.message}</p>
        ))}
      </div>

      <div className="search-bar">
        <input type="text" placeholder="Search charities..." />
      </div>

      <div id="foodbanks-list">
        {charities.length === 0 ? (
          <p>No charities found in your region.</p>
        ) : (
          charities.map((charity, index) => (
            <div
              key={charity.stakeholderid}
              className={`offer-item ${
                index % 2 === 0 ? "green-bg" : "white-bg"
              }`}
            >
              <div className="offer-details">
                <h4>{charity.name}</h4>
                <p>{charity.region}</p>
              </div>
              <div className="offer-actions">
                <button
                  className="btn btn-donate"
                  onClick={() => handleDonate(charity)}
                >
                  {userType === "Business" ? "Offer Donation" : "Donate"}
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Donor;
