// src/users/HomeForm.jsx
import React, { useState } from "react";
import axios from "axios";

export default function HomeForm({ currentUser, onClose }) {
  const [form, setForm] = useState({ name: "", expiryDate: "", quantity: 1, foodCategory: "" });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.name) return setError("Name is required");
    if (new Date(form.expiryDate) <= new Date()) return setError("Expiry date must be in the future");

    try {
      await axios.post("https://foodsave-backend.onrender.com/api/users/fooditems", {
        ...form,
        stakeholderID: currentUser.stakeholderID
      });
      setSuccess("Food item added!");
      setError("");
      setForm({ name: "", expiryDate: "", quantity: 1, foodCategory: "" });
    } catch (err) {
      setError(err.response?.data?.error || "Failed to add item");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="form-card">
      <h1>Add Food Item</h1>

      <input type="text" name="name" placeholder="Name" value={form.name} onChange={handleChange} required />
      <input type="date" name="expiryDate" value={form.expiryDate} onChange={handleChange} required />
      <input type="number" name="quantity" min="1" value={form.quantity} onChange={handleChange} required />
      <input type="text" name="foodCategory" placeholder="Category (optional)" value={form.foodCategory} onChange={handleChange} />

      {error && <p className="error-msg">{error}</p>}
      {success && <p className="success-msg">{success}</p>}

      <button type="submit">Add Item</button>
      <button type="button" className="toggle-link" onClick={onClose}>Back to Inventory</button>
    </form>
  );
}
